}

/* moves progress bars under monster's stats */
.battle-end-stats .registration-well > .row:nth-child(4) {
    top: 553px;
}

/*condenses things down to five abilities per row*/
.equipment-slot {
    margin: 0 11px 20px 0;
    border: 0;
    width: 130px;
    height: 180px;
    vertical-align: top;
    padding: 6px;
    position: relative;
}



/*spacing equipment slots*/
.equipment-wrap {
    overflow: hidden;
}


/*spacing equipment slots*/
.equipment-wrap .row {
    padding: 0;
    margin: 0;
}

/*spacing equipment slots*/
.equipment-slot:last-child {
    margin-right: 0;
}

/*move charge counter + readability */
.equipment-slot .label-info {
    position: absolute;
    top: 0;
    right: 0;
    background: none;
    color: #3b4e51;
    font-size: 16px;
    height: 24px;
    line-height: 20px;
    border: 1px solid #37317e;
    padding: 0 6px;
}

/*condenses loot*/
.battle-loots {
    margin: 15px 0 0;
    padding: 0;
}

/*condenses loot*/
.battle-loot {
    height: auto;
}

/*consumables*/
.battle-end-stats .row.text-center {
    position: absolute;
    top: 649px;
    right: 20px;
    width: 215px;
    margin: 0;
    font-size: 0;
}

/*consumables*/
.battle-end-stats .row.text-center img {
    max-width: 70px;
}

/* back to warrior button */
.battle-complete > p .btn {
    position: absolute;
    right: 20px;
    width: 215px;
    top: 597px;
}

/* continue battling - sizing */
.battle-complete > p > .btn[href*="generate"] {
    position: absolute;
    top: 377px;
    width: 716px;
    left: 20px;
    height: 86px;
    line-height: 86px;
}

