data.css:
@-moz-document domain("mangakakalot.com"), domain("manganelo.com") {
/* main background */
html,
body {
    background: #111;
}

.top-logo,
.logo,
.logo_chapter {
    mix-blend-mode: multiply;
}

.logo_chapter {
    background: #111;
}

.top-header,
.container-header-top {
    background: #333!important;
}

.searching form input,
.box-search form input,
.box-search {
    background-color: #333;
    color: #ccc;
}

#search_result ul a li,
#SearchResult ul a li {
    background: #333;
    color: #ccc;
    border-color: #777;
}

#SearchResult ul a li .item_name,
#SearchResult ul a li .item_author,
#SearchResult ul a li .item_chapter {
    color: #ccc;
}

li[class$="selected"] {
    background: #2a2a2a!important;
    color: #ccc!important;
    border-color: #777!important;
}

/* home page */
.doreamon,
.panel-content-homepage,
.panel-newest-content {
    background: #333!important;
}

.content-homepage-title {
    border-color: #777!important;
}

.doreamon div ul li span a {
    color: #ccc;
}

.content-homepage-item .item-title a:not(:hover) {
    color: #ccc;
}

.content-homepage-item .item-chapter a:not(:hover) {
    color: #ddd;
}

.panel-category table tbody tr,
.panel-category table,
.panel-category p {
    background: #111!important;
}

.panel-category table tbody tr:nth-child(even),
.panel-category p:nth-child(even) {
    background: #333!important;
}

.pn-category-row-border {
    border-color: #3498db!important;
}

.panel-category table tbody tr td a,
.panel-category p a:not(:hover) {
    color: #ccc;
}

.panel-advanced-search-tool {
    background: #333;
    color: #ddd;
}

span[class*="choose"] {
    color: #ddd!important;
}

.advanced-search-tool-keyword-content,
.advanced-search-tool-orderby-content,
.advanced-search-tool-status-content,
.advanced-search-tool-keyword-type,
.advanced-search-tool-link {
    background: #333!important;
    color: #ccc!important;
    border-color: #777!important;
}

.container-footer-content {
    background: #333!important;
    color: #ddd;
}

#tooltip {
    background: #333;
    border-color: #777;
}

#tooltip h3 {
    color: #ccc;
}

.tooltip_detail_left p,
.tooltip_summary{
    color: #ddd!important;
}

/* latest updates / hot manga / new manga / completed manga */
/* cards where the manga info is in */
.list-truyen-item-wrap,
.content-genres-item {
    background: #333!important;
}

/* description of manga */
.list-truyen-item-wrap p,
.genres-item-description {
    color: #ddd!important;
}

/* title of manga */
.list-truyen-item-wrap h3 a,
.content-genres-item h3 a {
    color: #ccc!important;
}

.genres-item-description-linear {
    filter: invert(80%)!important;
}

/* search */
.story_item,
.search-story-item,
.panel-search-story,
.panel-not-found {
    background: #333;
}

.story_item h3 a:not(:hover),
.search-story-item h3 a:not(:hover) {
    color: #ccc!important;
}

.story_item span,
.search-story-item span,
.panel-not-found p {
    color: #ddd!important;
}

/* when clicked on a manga */
/* main card background */
.manga-info-top,
.container-main-left .panel-breadcrumb,
.panel-story-info {
    background: #333;
    color: #ccc!important;
    border-color: #777;
}

/* main card text (important needed to overwrite color) */
.manga-info-text li,
#rate_row_cmd {
    color: #ccc !important;
}

/* description background and color (important needed to overwrite color) */
#noidungm {
    background: #333 !important;
    color: #ccc !important;
}

/* show more / show less background */
.content-extent {
    background: #333;
}

#content-extent-linear i,
#panel-description-linear i {
    filter: invert(80%)!important;
}

.chapter_issue {
    background: #333!important;
}

/* chapter list background */
.manga-info-chapter,
.panel-story-chapter-list {
    background: #333;
}

/* chapter list */
.chapter-list span a,
.chapter-list div span,
.panel-story-chapter-list ul li,
.panel-story-chapter-list ul li a[rel="nofollow"] {
    color: #ccc;
}

/* comment section - cant change text color since its an iframe */
.comment-info,
.panel-story-comment,
.panel-fb-comment{
    background: #333;
}

div[style="background: #FFF; float: left; margin-top: 10px;padding: 10px; width: calc(100% - 20px);"] {
    background: #333!important;
}

/* chapter */
.logo_chapter,
.panel-logo-chapter,
.info-top-chapter p,
.info-top-chapter h2,
.panel-chapter-info-top h1,
.panel-chapter-info-top p,
.current-chapter {
    display: none!important;
}

.option_wrap,
.panel-navigation {
    background-color: rgba(0,0,0,.39);
}

.navi-change-chapter {
    background: #333;
    color: #ccc;
    border-color: #777!important;
}

.comments,
.new_update_trangdoc,
.panel-chapter-info-bot {
    background: #333;
    color: #ddd;
    border-color: #777;
}

.update_item h3 a {
    color: #ccc!important;
}

/* profile */
.user-panel-left,
.user-panel-setting {
    background: #333!important;
    color: #ccc!important;
}

.user-panel-left span[style="color: #000;"]{
    color: #ccc!important;
}

.user-panel-setting ul li a {
    color: #ccc!important;
}

.changes_info input,
.changes_info textarea {
    background: #333!important;
    color: #ddd!important;
}

/* bookmark */
.bookmark_item,
.bookmark-item {
    background: #333!important;
}

.bookmark_item span,
.item-story-name, 
.item-title {
    color: #ccc!important;
}

.item-title a {
    color: #ddd!important;
}
}

@-moz-document domain("bing.com") {
::-webkit-scrollbar {
 	/* Width */
 	width: 1.2em!important;
 }

 /* Color of handle ___________________________________________________________________________*/

 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track ______________________________________________________________________________*/

 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom ___________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of the bottom corner of the scrollable element, where two scrollbar meet. ______________*/

::-webkit-scrollbar-corner
 {
 	background: #530205!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

::-webkit-scrollbar-corner:hover
 {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }
}

