/**
* This stylesheet makes some needed minor changes to make YouTube Music's control-bar feel more responsive and easier to use.
 **/
@-moz-document url(https://music.youtube.com/) {	

  html, body {
    background: #1D1D1D !important; 
  }

  .ytmusic-player-page {
      /*Remove this line to remove the gradient background, if you so prefer*/
      background-image: linear-gradient(#181818, #181818) !important;  
  }


  /* Right controls */
  /* show-currently-playing button */
  .toggle-player-page-button  {
      border-radius: 60px;
      width: 55px !important;
      height: 55px !important;
      transition-duration: 0.5s !important;
  }
  .toggle-player-page-button > * {
      background-color: rgba(255, 0, 0, 0.5);
      border-radius: 60px;
      transition-duration: 0.2s !important;
  }
  .toggle-player-page-button:hover > * {
      background-color: rgba(255, 0, 0, 1) !important;
      transition-duration: 0.2 !important;
      transform: scale(1.1) !important;
  }

  /* Misc right-side buttons */
  .volume, .repeat, .shuffle {
      transition-duration: 0.07s;
  }
  .volume:hover, .repeat:hover, .shuffle:hover {
      background-color: #383838;
      border-radius: 50px;
  }
  .volume:active, .repeat:active, .shuffle:active {
      transform: scale(1.05);
  }
  #sliderKnob {
      cursor: pointer;
  }


  /* Left controls */ 
  /* Play-pause button */
  #play-pause-button {
      border-radius: 50px;
      background-color: red;
      transition-duration: 0.1s !important;
  }

  #play-pause-button:hover, .previous-button:hover, .next-button:hover {
      transform: scale(1.1) !important;
  }

  #play-pause-button:active, .previous-button:active, .next-button:active {
      transform: scale(0.95) !important;
  }
  /* <- & -> buttons */
  .previous-button, .next-button{
      transition-duration: 0.07s;
      background-color: rgba(255,0,0,0.6);
      border-radius: 50px;
  }
  /* Repositioning music controls */
  #left-controls {
      margin-left: 1vw !important;
  }



  /* Like, dislike & menu */
  .dislike {
      margin-right: -5px !important;
      margin-left:  25px !important;
  }
  .dislike, .like, .menu>* {
      transition-duration: 0.07s;
      border-radius: 7px;
  }
  .dislike:hover{
      color: tomato;
  }
  .like:hover{
      color: rgb(51, 153, 51);
  }


}

