.postbtn-reply-href {color: var(--theme_default_alttext); font-size: 1.1em; margin-left: 2px;}

.post__number {color: var(--theme_default_alttext2); font-size: 1.1em;}

.post__btn {fill: var(--theme_default_alttext2) !important; height: 18px; width: 18px; vertical-align: -4px; transition: .1s;}
