@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tempusinfinitumblog.com")
{

/*!========================================\\
|| Dark TempusInfinitumBlog                ||
||-----------------------------------------||
|| Created by ARH                          ||
||-----------------------------------------||
|| A simple dark theme for redlightnovel   ||
|| Especially usefull when reading at night||
||-----------------------------------------||
|| contact: hakerti@wp.pl                  ||
\\----------------------------------------!*/


/* sections manipulation */
#comments { display: none; }
nav.post-navigation { display: none; }


/* width manipulation */
#content { width: 90%; }
#primary { width: 80%; }
#secondary { width: 20%; }
#main { margin: 20px; }
p { margin: 0px; }

/* colors manipulation */
body { color: #ddd; }
.entry-title { color: #999; }
a { color: #69e !important; }
body { background-color: #222; }

}

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("readlightnovel.org")
{

/*!========================================\\
|| Dark RedLightNovel                      ||
||-----------------------------------------||
|| Created by ARH                          ||
||-----------------------------------------||
|| A simple dark theme for redlightnovel   ||
|| Especially usefull when reading at night||
||-----------------------------------------||
|| contact: hakerti@wp.pl                  ||
\\----------------------------------------!*/
/*!========================================\\
|| special thanks to 2 other dark styles   ||
||   and their creators:                   ||
|| - thenonymous                           ||
||     userstyles.org/styles/168654        ||
|| - Kristin Sporina                      ||
||     userstyles.org/styles/174415        ||
\\----------------------------------------!*/

/* width manipulation */
.container { width: 90%; }
.container--content > .row > .content2 { width: 80%; }
.container--content > .row > .sidebar { width: 20%; }

/* colors manipulation */
.container { color: #ddd; }
.black-link { color: #999; }
.sidebar .popular-novels .popular-novel-item-block .popular-novel-item-top a input { color: #bbb; }
.ob-widget-header{ color: #ddd !important; }
.ob-rec-text { color: #ddd !important; }
.container--content { background-color: #222; }
.chapter-actions { background-color: #444; }
.chapter-actions, select { background-color: #444; }
.header .header-search { background-color: #444; }
.autocomplete { border-color: #666 !important; }
input { color: #999 !important; }
.header-middle { background-color: #666 !important; }
.alert-danger > a { color: #69e; }
.alert-danger {
    background-color: #844;
    border-color: #f44;
    color: #f99;
}

/* restore selectability */
.chapter-content3 {
    -webkit-touch-callout: auto !important;
    -webkit-user-select: auto !important;
    -khtml-user-select: auto !important;
    -moz-user-select: auto !important;
    -ms-user-select: auto !important;
    user-select: text !important;
}

/* sections manipulation */
.apester-element { display: none; }

}

