    background-color: rgba(0, 0, 0, 0.2);
}

.PlayerControlsNeo__transport-controls {
    opacity: 0.3;
}

.PlayerControlsNeo__transport-controls:hover {
    opacity: 1;
}

.PlayerControlsNeo__gradient {
    background: none !important;
}

.mainView {
    padding-top: 3rem;
}

.profile-icon {
    border-radius: 5%;
}

