}
}

@-moz-document url("https://web.whatsapp.com/") {
:root {
    /* Discord colors */
    --background:          #202225;
    --background-text:     #706E75;
    --main:                #37393F;
    --main-text:           #D7D5D4;
    --main-text-dim:       #73767B;
    --main-highlight:      #33353B;
    --main-secondary:      #41444B;
    --main-secondary-text: #63767D;
    --main-text-icon:      #B9BBBE;
    --sidebar:             #2F3136;
    --sidebar-text:        #8F9297;
    --sidebar-hover:       #34363C;
    --white:               #F3F3F3;
    --black:               #18191C;
    --discord-icon:        #4EB681;
    --link:                #3BA3ED;
    --border-radius:         3px;

    /* Overwrite Whatsapp variables */
    --panel-background: var(--sidebar);
    --panel-background-lighter: var(--sidebar);
    --panel-background-deeper: var(--background);
    --panel-header-background: var(--main);
    --primary: var(--background-text);
    --primary-strong: var(--white);
    --primary-stronger: var(--main-text);
    --secondary: var(--background-text);
    --secondary-lighter: var(--sidebar-text);
    --secondary-stronger: var(--sidebar-text);
    --icon: var(--background-text);
    --teal: var(--discord-icon);
    --background-default: var(--sidebar);
    --background-default-active: var(--main);
    --background-default-hover: var(--main-secondary);
    --border-chatlist: transparent;
    --icon-lighter: var(--sidebar-text);
    --unread-marker-background: var(--discord-icon);
    --icon-search-back: var(--discord-icon);
    --border-stronger: var(--background);
    --conversation-panel-background: var(--main);
    --outgoing-background: var(--main-secondary);
    --outgoing-background-deeper: var(--main-secondary);
    --incoming-background: var(--main-secondary);
    --incoming-background-deeper: var(--main-secondary);
    --rich-text-panel-background: var(--sidebar);
    --compose-background: var(--main-secondary);
    --compose-panel-background: var(--main);
    --input-placeholder: var(--main-secondary-text);
    --border-panel: transparent;
    --overlay-rgb: transparent;
    --incoming-background-rgb: transparent;
    --outgoing-background-rgb: transparent;
    --dropdown-background: var(--main);
    --system-message-background: transparent;
    --intro-background: var(--main);
    --panel-background-deep: var(--background);
    --panel-background-colored: var(--background);
    --button-round-background: var(--main-secondary-text);
    --button-alternative: var(--discord-icon);
    --inverse: var(--main);
    --media-viewer-button-background: var(--main);
    --media-viewer-button-icon: var(--main-text);
    --media-viewer-background: var(--sidebar);
    --icon-strong: var(--sidebar-text);
    --gray-150: var(--discord-icon);
    --app-background-stripe: var(--background);
    --border-list: transparent;
    --message-primary: var(--main-text);
    --compose-primary: var(--main-text);
    --search-input-background: var(--main-secondary);
    --search-container-background: var(--main);
    --compose-input-background: var(--main);
}

/* Sidebar */
._1ecJY {
    padding: 8px 0;
}

/* Sidebar contact */
._2EXPL {
    border-radius: var(--border-radius);
    margin: 0 8px 2px 8px;
