css:
@-moz-document domain("podricing.org") {
body > header {

    background: -moz-linear-gradient(center top , #41688d 0%, #41688d 100%) repeat scroll 0 0 rgba(0, 0, 0, 0) !important;

border-bottom: 0px solid #000000 

!important;

}

body {
background: url('http://i.imgur.com/DBfXPdn.jpg') fixed !important;
}

body > header a {

    color: #FFFFFF !important;

}



body > header .header-nav > span {

    border-left: 0px solid #FFFFFF !important; 

    border-right: 0px solid #FFFFFF !important;

}





body > header #nav_badges #notification_dropdown a {

    color: #a7cbee !important;

}



body > header #nav_badges #notification_dropdown {

    background: none repeat scroll 0 0 #41688d;

    border: 0px solid #999999;

}







body > header #nav_badges #notification_dropdown .notification_element:hover {

background-color: #eaf5ff !important

}





body > header #nav_badges #notification_dropdown .notification_element, body > header #nav_badges #notification_dropdown .header {

    background-color: #dff3e0

!important;

    border-bottom: 1px solid #d8e2ec

!important;

}



body > header #global_search form input:hover {

background-color: #f4f4f4 !important}



body > header #global_search form input:focus {

background-color: #f4f4f4 !important}



body > header #global_search form input {

    background-color: #f4f4f4 !important;

    border: 1px solid #3fa542 !important;

    box-shadow: none !important;

}



.ac_over {

    background-color: #325a1f !important;

}



body > header ul#user_menu {

    border-left: 1px solid rgba(0, 0, 0, 0) 

!important;

    border-right: 1px solid rgba(0, 0, 0, 0)

!important;

color: #FFFFFF !important;

text-shadow: none !important;

}





body > header .user-menu-avatar {

    background-color: #325a1f

!important;

    border: 0 solid rgba(0, 0, 0, 0)

!important;

    border-radius: 3.5px !important;

}



body > header ul#user_menu.active {

    background: none repeat scroll 0 0 #69d662 !important;

    border: 1px solid #325a1f !important;

}



body > header .user-menu-item a {

    color: #FFFFFF !important;

}



body > header .user-menu-item a:hover {

    background-color: #325a1f !important;

color: #FFFFFF !important;

}



.branding-header-logo {

    background-position: 0 0 !important;

}



.icons-notifications_grey {

    background-position: 0 -2367px !important;

}



.icons-mail_grey {

    background-position: 0 -2546px !important;

}



.avatar {

    background-color: #325a1f

!important;

    border: 0 solid rgba(0, 0, 0, 0)

!important;

    border-radius: 7px

!important;

}





#home_user_badge h4 a {

color: #4d9959 !important;

}



#leftNavBar .selected a {

    color: #4d9959 !important;

}



#leftNavBar a {

    color: #4d9959 !important;

}





#leftNavBar .hoverable:hover {

background-color: #325a1f !important;

}





.stream_container {

    border-left: 1px solid #325a1f

!important;

}



h3 {

    color: #63ac56 !important;

}





#publisher_textarea_wrapper {

   border: 1px solid #325a1f !important;

}



#publisher_textarea_wrapper.active {

    border: 1px solid #325a1f !important;

}



#publisher {

    border-bottom: 1px solid #325a1f !important;

}



.post_preview {

    background-color: #f2fff1 !important;

}



.tag:hover {

background-color: #ffffff !important

}



*::-moz-placeholder {

    color: #5fac56 !important;

text-shadow: none !important;

}



.comment.media {

    border-top: 1px solid #5fac56 !important;

}



a {

    color: #5fac56 !important;

}



a:hover {

color: #63ac56 !important;

}



.stream .stream_element {

    border-bottom: 1px solid #325a1f

!important;

}



.stream_element .post-content .reshare {

    border-left: 2px solid #325a1f !important;

}



.opengraph a {

    border-bottom: 1px solid #325a1f

!important;

    border-top: 1px solid #325a1f

!important;

}





.show_comments {

    border-top: 1px solid #325a1f 

!important;

}



form.new_comment textarea {

    border: 1px solid #325a1f !important;

}



input[type="text"]:focus, input[type="password"]:focus, input.text:focus, input.title:focus, textarea:focus, select:focus {

    border-color: #5bac56 !important;

}





#back-to-top {

    background-color: #214f14 !important;

}



.button.creation {

    background-image: -moz-linear-gradient(center top , #325a1f 0%, #214f14 100%) !important;

}





.button.creation:hover {

    background-image: -moz-linear-gradient(center top , #325a1f 0%, #214f14 100%) !important;

}



.rightBar .title h5 {

    color: #325a1f !important;

}



.rightBar .title {

    border-bottom: 1px solid #325a1f !important;

}



body > header #nav_badges #notification_badge.active {

  background-color: rgba(0, 0, 0, 0) !important;

    border-color: rgba(0, 0, 0, 0) !important;

}



hr {

    background: none repeat scroll 0 0 #325a1f !important; 

    border: 1px solid #325a1f !important;

}





.item_count {

    background-color: #214f14 !important;

    color: #7dc678 !important;

}





#single-post-interactions {

    border-left: 1px solid #325a1f !important;

}



#single-post-interactions .comments .comment {

    border-bottom: 0px solid #CCCCCC !important;

}



#left_pane {

    border-right: 1px solid #effdec !important;

}



#left_pane #left_pane_header {

    border-bottom: 1px solid #325a1f !important;

}

.stream_container 
{
background-color: rgba(255, 255, 255, 0.7) !important;
}

#container
{
background-color: rgba(255, 255, 255, 0.7) !important;
}

#left_pane {
padding-top: 15px !important;
margin-top: 15px!important;
background-color: rgba(255, 255, 255, 0.7) !important;
border-right: 1px solid #effdec !important;
}

.rightBar.last
{
margin: 0px 0px -12px -30px !important;
background-color: rgba(255, 255, 255, 0.7) !important;
}
#leftNavBar {
padding-top: 8px !important;
padding-left: 8px !important;
margin-right: 25px !important;
width: 175px !important;
background-color: rgba(255, 255, 255, 0.7) !important;
}

div.span-24 {
background-color: rgba(255, 255, 255, 0.3)!important;
margin: -24px 0px -15px -10px !important;
padding: 20px 15px 25px 40px !important;
border-bottom-left-radius: 5px !important;
border-bottom-right-radius: 5px !important;
}
}
