css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("https?://(www.)?torrentsm((d.(com|eu|me))|(oldova.(com|org|net))).*") {
body, div.sp-body.inited {
    background: url(http://s41.radikal.ru/i091/1108/3d/c2e19a2f57da.png)no-repeat fixed center center #000;
    background-size: cover;
}
#torrents > table > tbody > tr  {
    background-color:transparent !important;
}

input {
    -moz-appearance: none;
    background: 0 100%;
    border: none;
}

input:hover {
    -moz-appearance: none;
    background: 0 100%;
}

#user_box * {
    color: #0ceb47;
}

table {
    background: url(http://s41.radikal.ru/i091/1108/3d/c2e19a2f57da.png)no-repeat fixed center center #000;
    background-size: cover;
}

table td {
    background: url(http://s41.radikal.ru/i091/1108/3d/c2e19a2f57da.png)no-repeat fixed center center #000;
    background-size: cover;
}

a {
    color: lime;
}

a:hover {
    color: blue;
}

tbody tr td {
    border-style: none;
    color: lime;
}

div.mCenter.categories {
    background: url(http://s41.radikal.ru/i091/1108/3d/c2e19a2f57da.png)no-repeat fixed center center #000;
    background-size: cover;
    border-style: none;
}

span {
    border-color: #edd5ed;
}

div#top_menu>table>tbody>tr>td:first-child>a {
    display: block ;
    background: url("http://i.imgur.com/Q8CBk.png") no-repeat scroll 0 0 
transparent ;
    width: 280px ;
    height: 76px ;
}

div#top_menu>table>tbody>tr>td:nth-child(2)>table:first-child>tbody>tr>td {
    color: #F1F1F1 ;
}

div#top_menu>table>tbody>tr>td:nth-child(2)>table:first-child>tbody>tr>td>a {
    color: #124d89 ;
    font-weight: bold ;
    text-indent: -8px ;
    font-family: arial,sans-serif ;
    text-transform: lowercase ;
}

#tmd_logo {
    display: none ;
}

.lnk {
    color: yellow;
}

}
