@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("clubic.com") 
{
    #colRight, #pub_dart_MBR_T_728x90, #liensTransversaux, #colMiddle, #shopper-widget-wrapper, #pub_dart_MBR_B_728x90, #annonce_pub_contenu, #fb_twitter_page_header, .news_bottom, #fb_like_group_header, #pub_dart_LIS_T2_5x5_ad_container, #colonne_widgets, .contenu_block_central.noBackground, #mb_container, .ob_container, #aLireTitre, #aLireAussi, #adsense, .block-adsense, .selection_af, #pousse_clic, #inshop
    {
        display: none !important;
    }
    
    .sprite-global.lastMenu_png 
    {
        border-radius: 0px 0px 6px;
    }
    
    .contenu_principal .block_central
    {
        width: 1000px;
    }
    
    #colPremium ul
    {
        border-radius: 0px 0px 8px;
    }
    
    #colLeft
    {
        width: 75%;
        margin-left: 3%;
        margin-top: 15px;
        margin-bottom: 60px;
    }
    
    #sinformer #news li .news_content_width3 .news_title
    {
        width: 600px;
    }
    
    #sinformer #news li .news_title
    {
        float: none;
    }
    
    #sinformer #news li .news_desc .img
    {
        margin-right: 30px;
    }
    
