css:
/*BY yaopk*/
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://www.baidu.com/"),url-prefix("http://www.baidu.com/index")
{
/*隐藏多余项*/
#lg,.tools,#mMenu,.widget-ads,#ftCon,#lm,.ws-tips{display:none!important;}

/*背景*/
#content{background:url(http://static.bbs.maxthon.cn/data/attachment/forum/201404/06/220920drbqwvzvhdpvpiqh.jpg) no-repeat center top;}

/*用户栏*/
#u1,#u{height:30px;padding:0;padding-right:10px;line-height:30px;background-color:rgba(255,255,255,.5);}
#u1 a,#u a{color:#000;text-decoration:none;}

/*主体*/
#m{width:590px!important;margin-top:188px;}
#content{height:100%;padding:0;font-family: "Microsoft yahei","微软雅黑";}
#wrapper{height:100%;}
body{background-color:#fcfcfc;}

/*导航*/
#nv{width:591px;font-size:0;text-align:center;text-indent:0;margin:0;height:55px;padding-left:15px;}
#nv{border-top-left-radius:5px;border-top-right-radius:5px;background:-webkit-linear-gradient(top,#974C22 40%,#8D431E 80%,#843F18);}
#nv a,#nv b{display:block;float:left;width:70px;height:55px;line-height:55px;font-size:18px;color:#fff;text-decoration:none;font-weight:400;border-right:1px solid #81360E;border-left:1px solid #AA6138;}
#nv a:hover,#nv b{background:-webkit-linear-gradient(top,#b31609 60%,#a91404 80%,#9e0f00);}

/*搜索框*/
#fm{width: 604px;height:61px;padding:15px 0 0 0;position:relative;border:1px solid #ccc;border-bottom-left-radius:5px;border-bottom-right-radius:5px;background:#fcfcfc;-webkit-filter:drop-shadow(0 1px 16px rgba(0,0,0,.2));}
#fm .s_ipt_wr{width: 430px;margin:3px 0 0 50px;height:40px;padding:0 5px;border:1px solid #ccc;border-right:none;border-top-left-radius:5px;border-bottom-left-radius:5px;background:#FDFDFF;}

/*输入框*/
#fm .s_ipt{width:100%;height:100%;font:18px/18px "微软雅黑";margin:0;color:#9a9a9a;background:#FDFDFF;}

/*按钮*/
#fm .s_btn_wr{width:60px;height:42px;background:#B42505;border-top-right-radius:5px;border-bottom-right-radius:5px;position:absolute;top:3px;right:53px;transition:0.5s;}
#fm .s_btn_wr:hover{transition:1s;background:#E3340F;}
#fm .s_btn{width:60px;height:42px;font-size:0;background:url(http://static.bbs.maxthon.cn/data/attachment/forum/201403/31/230940qgcsbps2xkdqzsc2.png) no-repeat center;}

/*搜索建议*/
#fm .bdsug{width:440px;margin-left:50px;top:42px;border:1px solid #ccc;border-top:none;border-bottom-left-radius:5px;border-bottom-right-radius:5px;}
#fm .bdsug li{width:424px;padding:5px 8px;color:#B92403;font-weight:600;transition:1s;}
#fm .bdsug li:hover{background-color:#FCA793;transition:0.2s;}
#fm .bdsug b{color:#000;padding-left:3px;font-weight:200;}

/*链接*/
#lk{margin-top:50px!important;color:#fff;}
#lk a{display:inline-block;color:#fff!important;width:100px;height:40px;text-align:center;line-height:40px;text-decoration:none;border-radius:5px;background-color:#BA2401;transition:0.5s;}
#lk a:hover{background-color:#E3340F;transition:0.2s;}
}
