css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("about:newtab") {
    .newtab-cell:not([dragged]) .newtab-site {
        box-shadow:none !important;
    }
/*    .newtab-site {
        transition:none;
    }*/
    .newtab-cell:hover .newtab-control.newtab-control-pin[pinned],
    .newtab-cell:hover .newtab-control.newtab-control-block {
        opacity:0;
    }
    .newtab-control.newtab-control-pin:hover,
    .newtab-control.newtab-control-block:hover {
        opacity:1 !important;
    }
    
    .newtab-title {
        opacity:0.5;
        text-align:center;
        margin:3px;
        border-radius:8px;
        line-height:1.6em;
        font-family:Arial;
    }
/*    .newtab-cell:hover .newtab-title {
        opacity:0.8;
    }*/
    #newtab-scrollbox {
        background: linear-gradient(to bottom, #ffffff 0%,#e5e5e5 100%);
    }
    
    .newtab-thumbnail {
        background-size:120%;
        background-position:top center;
        opacity:1;
    }
    
 /*    .newtab-cell,
    .newtab-site,
    .newtab-thumbnail{
        border-radius:60px 0;
        overflow:hidden;
    }*/
    
    #newtab-margin-top {
        margin-bottom:3em;
    }
    #newtab-margin-bottom {
        margin-top:3em;
    }
    
/*    .newtab-control.newtab-control-pin {
        left:auto;
        right:28px;
    }*/
}
