css:
@-moz-document url-prefix("http://zc2.ayakashi.zynga.com/") {
.ui-mobile-viewport,body{
  margin-right:auto!important;
  margin-left:auto!important;
}
#drama-page {
overflow-y: hidden;
}
.opening-window {
  height:initial!important;
  }
body{
  background-color : transparent !important;
  background: url("http://img4.wikia.nocookie.net/__cb20140611081003/ayakashi-ghost-guild/images/6/69/Mira_Anniversary_PC_Wallpaper.jpg") no-repeat top right fixed !important; 
  /*background: url("http://img2.wikia.nocookie.net/__cb20130715100631/ayakashi-ghost-guild/images/d/d1/Satori_wallpaper_%28FB%29.jpg") no-repeat center right fixed !important; */
  /*background: url("http://img4.wikia.nocookie.net/__cb20140112021002/ayakashi-ghost-guild/images/0/01/Top_8_Ameno_Uzume_%28All_Forms%29_wallpaper.jpg") no-repeat center center fixed !important;*/
  /*background: url("http://img2.wikia.nocookie.net/__cb20140121122910/ayakashi-ghost-guild/images/6/6d/Mira_Wallpaper_ranking.jpg") no-repeat top left fixed !important;*/
    /*background: url("http://img1.wikia.nocookie.net/__cb20130930111804/ayakashi-ghost-guild/images/1/12/Kaguya_Hime_Wallpaper.jpg") no-repeat top left fixed !important;*/
  /*background: url("http://img1.wikia.nocookie.net/__cb20130922134749/ayakashi-ghost-guild/images/7/79/Lunar_Lucilda_Wallpaper.jpg") no-repeat center center fixed !important;*/
  /*background: url("http://img1.wikia.nocookie.net/__cb20140105085051/ayakashi-ghost-guild/images/f/ff/Udaijin_%28New_Year%27s_Eve%29_Wallpaper.jpg") no-repeat center center fixed !important;*/
  /*background: url("http://img3.wikia.nocookie.net/__cb20130715131621/ayakashi-ghost-guild/images/a/a4/HikoOri_Wallpaper.jpg") no-repeat center center fixed !important;*/
  -webkit-background-size: cover !important;
  -o-background-size: cover !important;
  background-size: cover !important;
  width: 320px!important;
  zoom:1.5;

}
#stage-page #stage-name,#stage-page #player-data .status-line .note, #stage-page #stage-data .status-line .note {

color: #cfcfcf!important;

}
.friend-list .status, .thumb-list-1, .friend-list, #monster-status, #selected-parts, #target-parts, .item-list, .parts-list {
  color: #dfdfdf;
  }
.system-window{
  top:0!important;
  }
  
div[data-role=dialog],div[data-role=page],#stage-page,#stage{
  min-height:440px!important;
  height:initiate;
}
#stage-complete-window{
  height:inherit!important;
}
.ui-page,#merge-scene,.ui-dialog-contain,div[data-role="dialog"] .ui-dialog-contain,.ui-page.extraquest-event,.ui-page[data-event-id],.ui-page[data-event-id] .ui-body-k{
  background:rgba(90, 75, 59, 0.74);
}

.ui-body-k.ui-dialog.fullscreen .ui-overlay-shadow,.ui-dialog-contain,#merge-scene{
  box-shadow: 0 0 15px 2px black;
  -webkit-box-shadow: 0 0 15px 2px black;
}
        
#merge-page{
  background:transparent;
  box-shadow:none;
  -webkit-box-shadow: none;
  margin-top:10%;
} 

div[data-role="page"] {
  box-shadow: 0 0 15px 2px black;
  -webkit-box-shadow: 0 0 15px 2px black;
  background:rgba(73, 49, 53, 0.7)!important;
}

#guild-raid-event-battle-confirm #friendly-force, #guild-raid-event-battle-lost #friendly-force,#island-list .island .master-info,#gacha-exchange-page .thumb-list-1 .card-condition,.gacha-list-elem .description,.gacha-list-elem .description-invite,#succession-gacha{
  background:rgba(73, 49, 53, 0.5)!important;
}

#activityList li ,#remove-confirmation-page .param-row,#skill-details, .navigator-comment,.frame-box,.gacha-list-elem,.stage,#sub-status, .normal-list li a, .normal-list dt a, .thumb-list-1,#status,.friend-list,  #selected-parts, #target-parts, .item-list, .parts-list ,#area-list .area .link-to-stage-list,#invitation-box,#monster-status{
  background:rgba(26, 45, 70, 0.5) !important;
}
#changed-user-status li, #guild-raid-event-battle-confirm #friendly-force .status > div{
  background:transparent;
}
#card-acquisition-page [data-role="content"], #treasure-acquisition-page [data-role="content"]{
  padding-bottom:3px;
}
option {
  text-shadow:0 0 2px gray;
  font-family:sans-serif !important;
  color:#dddddd;
  background: rgba(26, 45, 70, 0.65) !important;
}

.ui-body-k.ui-dialog.fullscreen .ui-dialog-contain{
  position: relative!important;
}
body.merge #merge-confirm-box .navigator-comment{
  margin-bottom:10px
}
div#payment-log-page {
  background: rgba(26, 45, 70, 0.65) !important;
}
#player-forums{
  display:none;
}
}
