css:
@-moz-document domain("tlk.io") {
/*
>Modified by Vaporeon
*/

.theme--day {
background-color: #1b1b1b;
}

.theme--day .post {
background-color: #585858;
}

.chat {
text-shadow: none;
}

.post.me {
text-shadow: none;
background-color: #535353;
}

.participants {
color: #1b1b1b;
font-size: 14px;
line-height: 14px;
border: 1px solid #b3b3b3;
}

.theme--day .tooltip, .theme--day .participants {
background-color: #535353;
}

.participant-list-item {
white-space: nowrap;
cursor: pointer;
text-shadow: none;
}

.message-input {
-webkit-appearance: none;
-moz-appearance: none;
appearance: none;
width: 100%;
display: block;
overflow: hidden;
resize: none;
font: inherit;
color: #d3d3d3;
padding: 10px 15px 10px 10px;
font-size: 20px;
line-height: 30px;
border-radius: 6px;
border: 1px solid rgba(0,0,0,0.1);
background-color: #535353;
box-shadow: none;
-webkit-transition: background-color 0.1s linear;
-moz-transition: background-color 0.1s linear;
-o-transition: background-color 0.1s linear;
transition: background-color 0.1s linear;
}
  

.theme--day .button {
color: #535353;
border-color: #1d1d1d;
}
  
.theme--day .button:hover {
color: #000000;
background-color: #535353;
}

.button {
color: #1d1d1d;
background-color: #535353;
  }
      
.counter {
position: relative;
margin: 0 4px;
padding: 4px 22px 4px 0;
font-size: 11px;
color: #1d1d1d;
border-radius: 0 0 6px 6px;
border: 1px solid #dbdbdb;
border-top-width: 0;
background-color: #535353;
text-align: center;
cursor: default;
}
      
.theme--day .counter {
background-color: #535353;
}
}
