css:
@-moz-document domain("wikipedia.org") {
* {
  font-family: sans-serif;
}
html {
  /* old size used to be 0.8; new size is 0.875; 0.8 = 0.875 * 0.914 */
  font-size: 0.915em;
}
div.thumbinner, .thumbcaption {
  font-size: 100% !important;
}
.infobox {
  font-size: 100% !important;
}
h1, .h1 {
  font-weight: bold;
  font-size: 2.2em;
  margin-top: 1em;
  padding-top: 0.5em;
  margin-bottom: 0;
  padding-bottom: 0;
}
h2, .h2 {
  font-weight: bold;
  font-size: 1.7em;
  padding-top: 1em;
  margin-top: 0;
  border-bottom: 1px solid #999;
  margin-bottom: 0.5em;
  padding-bottom: 0;
}
h3, .h3 {
  font-weight: bold;
  font-size: 1.6em;
  margin-top: 1em;
  border-bottom: 1px dashed #999;
  margin-bottom: 0.5em;
}
h4, .h4 {
  font-weight: bold;
  font-size: 1.4em;
  margin-top: 1em;
  margin-bottom: 0.5em;
}
h5, .h5 {
  font-weight: bold;
  font-size: 1.3em;
  margin-top: 1em;
  margin-bottom: 0.5em;
}
h6, .h6 {
  font-weight: bold;
  font-size: 1.2em;
  margin-top: 1em;
  margin-bottom: 0.5em;
}
}
