@-moz-document domain("fronter.com"){

/** Hovedbakgrunn **/
body { 
background: #000000;
}

/** Notifikasjonsomr√•de **/
.contentframe-actions-container { 
background: #000000;
text-align: right;
white-space: nowrap;
width: 100%;
}

/** Innhald hovedbakgrunn **/
tr.content-container td.content-container { 
background: #000000;
color: #CCCCCC;
padding: 10px 10px 15px;
}

/** Lenker **/
a.u-bluelink, a.black-link { 
color: #0187C5;
font-weight: 400;
}

/** Bakgrunnar objekt **/
table.seperated-tabs tr.content-container td.content-container td.content-container, table.seperated-tabs table, table.simple-content-frame table { 
background: #1A1A1A;
box-shadow: none;
}
/** Border midt inni **/
.clear-white-dashed { 
background: #282828;
border-bottom: 1px dotted #E0E0E0;
clear: both;
display: block;
height: 10px;
width: 100%;
}
/** Header background **/
.row-contents { 
background: #000000;
z-index: 2;
}

/** Header bottom side**/
#bottom-row { 
background: #000000;
height: 48px;
overflow: hidden;
position: relative;
}

/** Header top side **/
#top-row { 
background: #000000;
height: 50px;
position: relative;
}

/** Lenker i Notification **/
.container-inner.resettingtextformating a, a { 
color: #0187C5;
text-decoration: none;
}

/** Text in objects **/
.subscript-content-desc, .content-desc { 
color: #CCCCCC;
font-size: 11px;
}

/** Tab text color **/
