css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(rottentomatoes.com) {

* {
 text-decoration: none!important;
}

.tickets_link {
 margin-left: 0px!important;
}
        
.page_ad, .foot, #spotlightAd, [id*="_ad"] {
 display:none!important;
}

body, .body_main div, #nav_primary .subnav {
 background-color: #5A1F04!important;
}

.body, #overlay_body {
    background: url('http://images.rottentomatoescdn.com/images/backgrounds/site_bg_two.gif') repeat scroll left top #3F421D !important;
    position: relative;
}

.fb_button .fb_button_text, .title_box, .on-dvd-tab a, .in-theaters-tab a { /* Blue  */
 background-color: #3F421D !important;
} 

.fb_button .fb_button_text:hover, .body_main, .body_head, div.main_movie_area div.movie_poster_area ul.movie_links a.tickets_link:hover, .on-dvd-tab a:hover, .in-theaters-tab a:hover { /* Bright Blue  */
 background-color: #3F421D !important;
}

.lineHeader .fl, .media_block.movie_item:hover  { /* Red */
 background: -moz-linear-gradient(top, #000000 0%, #4c0000 50%, #000000 100%)!important;
} 

#overlay_body, .media_block .media_block_content, .media_block .media_block_content div, #nav_primary h2 {
 background: transparent!important;
}

#nav_primary h2:hover, #nav_primary .subnav_items li a:hover, .drop_menu > li.no-js:hover > .menu_label a, .drop_menu > li.hover > .menu_label a, #nav_primary .subnav_extra h4, #nav_primary .subnav_items li, h1, h2, h3, h4, h5, .content_box h4 a, table.movie_list td.middle_col a:hover, .content_header h3 a, .h3:hover, .content_header h3 a:hover, .content_header h1 a, .content_header h1 a:hover, .content_body.clearfix a:hover, .poster_box a, .bold, .comments_tbl th, .heading.clearfix h2 a, .poster_caption p, .media_block .media_block_content h3 a {
 color: #EEE!important;
}

.drop_menu > li > .subnav a, p, a, table.movie_list .tMeterScore, .rt_table .tMeterScore, table.movie_list td.right_col, table.movie_list th.right_col, div, .subtle, span {

 color: #EEE !important;

}

.poster_caption em {
 color: #EEE!important;
}

div.main_movie_area div.movie_poster_area ul.movie_links > li > a {
 color: #EEE !important;
}

img {
 opacity: .7!important;
}

img:hover {
 opacity: 1!important;
}

.drop_menu > li > .subnav {
    background-color: #5A1F04!important;
}

.rating_widget .review {
    background-color: #2D160C !important;
}

.autocomplete {
    background: none repeat scroll 0% 0% #5A1F04!important;
    border: 1px solid #D6D6D6;
}

.movie_list tbody tr:hover > td, .movie_list tbody tr:hover > th {
    background-color: #3F421D !important;
}

    
    
body.rt {
    background: url('http://images.rottentomatoescdn.com/images/backgrounds/site_bg_two.gif') repeat scroll left top #3F421D !important;
}
 
div.content_body {
    background-color: #5A1F04 !important;
}

table.proViewTbl tbody tr.alt, table.proViewTbl tbody tr.alt td {
    background: none repeat scroll 0% 0% #5A1F04 !important;
}

div.main div.content_body table.proViewTbl tr.alt td {
    background-color: #5A1F04 !important;
}    

table.proViewTbl tbody td, table.proViewTbl tbody th {
    background: none repeat scroll 0% 0% #5A1F04 !important;
}

table.proViewTbl tbody td, table.proViewTbl tbody th {
    background: none repeat scroll 0% 0% #3F421D !important;
}    

.content_abstract_tertiarynav_content {
    background: none !important;
    background-color: #3F421D !important;
}

table.proViewTbl thead th {
    background: -moz-linear-gradient(top, #3F421D 0%, #000000 100%)!important;
    border: solid 1px #CCC !important
}   

.head {
    background: linear-gradient(to bottom, #585C28 0px, #585C28 25%, #3F421D 100%) repeat scroll 0% 0% transparent !important;
}    

.content_abstract .white .content_abstract_content {
    border-left: 0px solid #B7BABB !important;
    border-right: 0px solid #BAB8B9 !important;
    background: none repeat scroll 0% 0% #3F421D !important;
}    

.snapshot ul.tab_area a.selected {
    color: #5A1F04 !important;
}
    
    
html > body .snapshot ul.tab_area a.selected {
    background-image: url('https://lh3.googleusercontent.com/-3xVgolAWwmM/U0O-L1SjxXI/AAAAAAAAAr8/d4ciUXgnGFM/w81-h159-no/corner_content_white_tabs.png') !important;
}    
    
html > body .content_abstract .white .content_abstract_corner1, html > body .content_abstract .white .content_abstract_corner2, html > body .content_abstract .white .content_abstract_corner3, html > body .content_abstract .white .content_abstract_corner4 {
    background-image: none !important;
    background-color: #3F421D !important;
}    

html > body table.proViewTbl_noHead thead th, html > body table.proViewTbl tfoot th {
    background-image: none !important;
}    

div.main div.content_body table.proViewTbl .friends_row td, div.main div.content_body table.proViewTbl .comments_row td {
    border-bottom: 1px solid #DDD !important;
}
div.main div.content_body table.proViewTbl tr td {
    border-top: 0px solid #B7BABB !important;
}    

table.textViewTbl tbody tr.alt td {
    background-color: #3F421D !important;
}    

html > body div#main_body_shadow {
    background: none !important;
    
}    
    
    
}
