@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");
@namespace hl url("http://www.w3.org/1999/xhtml");

/* #main-window { background-color: #303030 !important; }  */

#appmenu-button, #forward-button:hover:active {
  min-width: 20px !important;
  min-height: 19px !important;
  margin-left:  4px !important;
  background-image: -moz-linear-gradient(rgba(40,80,120,.4), rgba(40,80,120,.4), rgba(40,80,120,1), rgba(40,80,120,.7)) !important; }

#toolbar-menubar {
	background-image: -moz-linear-gradient(rgba(255,255,255,.8), rgba(255,255,255,.5), rgba(255,255,255,.9)) !important; }

#main-menubar {
background: transparent !important; }
  
#nav-bar {
	background-image: -moz-linear-gradient(rgba(255,255,255,.7), rgba(255,255,255,.8), rgba(255,255,255,.4), rgba(255,255,255,.8)) !important;
	background-color: transparent !important;
	border-bottom: 1px solid #aaaaaa !important;
	border-radius: 1px 1px 0 0 !important;
	margin: 0 !important;
	padding-bottom: 2px !important;
	height: 38px !important;
	visibility: visible !important; }  
	
#browser-bottombox, #TabsToolbar {
	background-image: -moz-linear-gradient(rgba(255,255,255,.9), rgba(255,255,255,.4), rgba(255,255,255,.7)) !important;
    background-color: rgba(255,255,255,.2) !important;
    border: none !important;
	border-radius: 1px 1px 0 0 !important;
	visibility: visible !important; } 
	
#browser-bottombox > toolbar {
    border-top: 1px solid #aaaaaa !important;
    height: 25px !important; }	
		
#urlbar	{
	background-image: -moz-linear-gradient(rgba(255,255,255,.89), rgba(255,255,255,.7), rgba(255,255,255,.4)) !important;
	background-color: transparent !important;
	border-color: #888888 !important;
	border-radius: 4px !important;
	visibility: visible !important; } 
	
#PersonalToolbar { 
	background-image: -moz-linear-gradient(rgba(255,255,255,.8), rgba(255,255,255,.55), rgba(255,255,255,.9)) !important;
    background-color: rgba(255,255,255,.2) !important;
    border-bottom: 1px solid #cccccc !important; } 	

.tabbrowser-tab:not([selected="true"]) {
	background-image: -moz-linear-gradient(rgba(200,200,200,.4), rgba(200,200,200,.3)) !important; }
.tabbrowser-tab:not([selected="true"]):hover {
	background-image: -moz-linear-gradient(rgba(255,255,255,.5), rgba(255,255,255,.1)) !important; }
.tabbrowser-tab[selected="true"] {
	background-image: -moz-linear-gradient(rgba(255,255,255,.9), rgba(255,255,255,.6), rgba(255,255,255,.6), rgba(255,255,255,.1)) !important; }
.tabs-newtab-button {
	background-image: -moz-linear-gradient(rgba(220,220,220,.2), rgba(220,220,220,.2)) !important; }
.tabs-newtab-button:hover {
	background-image: -moz-linear-gradient(rgba(255,255,255,.55), rgba(255,255,255,.1)) !important; }

#contentAreaContextMenu menuitem[disabled="true"], #context-bookmarklink,#context-sendlink,
#context-sendimage,#context-setDesktopBackground,#context-bookmarkpage,#context-sendpage
{display:none !important;}

sidebarheader {
	border: none !important;
	-moz-appearance: none !important;
	background-color: transparent !important;
	background-image: -moz-linear-gradient(rgba(255,255,255,.9), rgba(255,255,255,.6), rgba(255,255,255,.9)) !important; }
