css:
@-moz-document url("https://www.facebook.com/") {
#globalContainer {
  width: 100% !important; 
}

/* Left Sidebar */
#leftCol {
  background-color: #20364f; 
  padding-left: 20px !important;
  padding-right: 15px; 
  min-height:10000px;
}
.litestandClassicWelcomeBox a {
  color: #ccc !important;
}
.uiSideNav .item { 
  color: #888 !important;
}
span.countValue, span.maxCountIndicator {
  color: #eee !important;
}

/* Ads, events, people you may know... */
#rightCol {
  display:none;
}
#pagelet_ego_pane {
  display:none;
}

/* Chat sidebar */
.fbChatSidebar {
  width:250px; 
}
.fbChatSidebar .ticker_container {
  width: 250px !important;
}
.uiScrollableAreaBody {
  width:250px !important;
}

/* Main body area */
div#contentArea {
  width: 70% !important;
  margin-left: 7em;
  font-family: proxima-nova,"Helvetica Neue",Helvetica,Arial,sans-serif !important;
}
.mbs, ._6m7 {
  font-family: proxima-nova,"Helvetica Neue",Helvetica,Arial,sans-serif !important;
  font-size: 14px !important;
}
}
