@-moz-document domain("narutoforums.com") {





/** Removes the "Your Ad Here Links - Stolen From Golden Circle */    

div#pagenav_menu.vbmenu_popup + div { 

    display: none !important;

}

body {

    background: none repeat scroll 0 0 #b6c5d2 !important;
    color: #55b8d3 !important;
    padding: 0 !important;

}


a:link, body_alink {

    color: #408598 !important;

}

a:visited, body_avisited {

    color: #589cb5 !important;

}

a:hover, a:active, body_ahover {

    color: #32426c !important;

}

.page {
    background: none repeat scroll 0 0 #d4dfe9 !important;
    color: #1a629f !important;
    min-width: 950px;
    max-width: 100%;
    min-height: 20%;
    max-height: 80%;
    resize: both;
    width: 950px;
    margin-left: auto;
    margin-right: auto;
}

.tborder {

    background: none repeat scroll 0 0 #FFFFFF !important;
    color: #1e1e1e !important;

}

.tcat {

    background: url("http://i.imgur.com/oybC1OF.gif") repeat-x scroll left top #66DEFF !important;
    color: #ffffff !important;

}

.tcat a:link, .tcat_alink {

    color: #ffffff !important;
    text-decoration: none !important;

}

.tcat a:visited, .tcat_avisited {
    color: #ffffff !important;
    text-decoration: none !important;

}

.tcat a:hover, .tcat a:active, .tcat_ahover {

    color: #dceaf8 !important;
    text-decoration: underline !important;

}

.thead {
    background: none repeat scroll 0 0 #4d7190 !important;
    color: #ffffff !important;
}

