}

.tfoot a:visited, .tfoot_avisited {

    color: #B0E8F7 !important;

}

.tfoot a:hover, .tfoot a:active, .tfoot_ahover {

    color: #32426c !important;

}

.alt1, .alt1Active {

    background: none repeat scroll 0 0 #e9f1f8 !important;
    border: 1px solid #d9e5ef !important;
    color: #1e1e1e !important;

}

.alt2, .alt2Active {

    background: none repeat scroll 0 0 #d9e5ef !important;
    border: 1px solid #d9e5ef !important;
