css:
@-moz-document domain("www.youtube.com"), domain("youtube.com") {
/* Top bar */
html #masthead-container.ytd-app {
  position: absolute !important;
}

/* New YouTube UI as of Q4 2018 */
html ytd-watch-flexy[theater]:not([fullscreen]) #player-theater-container.ytd-watch-flexy,
html ytd-watch-flexy[theater]:not([fullscreen]) #player-theater-container.ytd-watch-flexy video {
    /*
    height: calc((9 / 16) * 100vw);
    max-height: calc(100vh - 169px);
    min-height: 480px;
    */
    height: calc(100vh - 56px) !important;
    max-height: calc(100vh - 56px) !important;
    min-height: 0 !important;
    top: 0 !important;
}
}
