css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("laquadrature.net") {
    body
    {
        background: #e1e6ef;
    }
    
    #sidebar
    {
        display:none;
    }
    
    #content.with-sidebar
    {
        float: none;
        width: 60%;
        max-width: 700px;
        
        background: #f2f2f2;

        padding: 5%;
        padding-right: 7%;
        padding-left: 7%;
        margin-left: 20%;
        margin-top: 50px;

    }
    
    p
    {
        font-family: Verdana;
        font-size: 1em;
        line-height: 1.2em;
        padding-top: 20px;
    }
}
