css:
@-moz-document domain("boards.4chan.org") {
body { min-width:800px; max-width:1000px; margin:0 auto; padding-top:65px; }

div#boardNavDesktop { position:absolute; top:0; right:0; left:0; min-width:800px; }
div#boardNavDesktop .boardList, div#boardNavDesktopFoot .boardList { display:block; text-align:center; background-color:#F1CEB6; color:#B86; padding:5px; }
div#boardNavDesktop .boardList a, div#boardNavDesktopFoot .boardList a { color:#000; }
div#boardNavDesktop #navtopright, div#boardNavDesktopFoot #navbotright { float:none; display:block; text-align:center; background-color:#800000; color:#800000; padding:5px; }
div#boardNavDesktop #navtopright a, div#boardNavDesktopFoot #navbotright a {color:#fff;}

.boardBanner{text-align:left; padding:5px;}
.boardBanner img{float:left;}

.board hr { border-top:4px solid #4E1908; }

.postContainer.replyContainer { width:100%; overflow:hidden; }
div.reply { display:block; }

.file a.fileThumb { transition:300ms; }
.file a.fileThumb:hover { opacity:0.8; }
.file.image-expanded a.fileThumb:hover { opacity:1; }
.file.image-expanded a.fileThumb img, .file video.expandedWebm {max-width:100%!important;}
.file video.expandedWebm { margin:3px 0px 5px; }
}
