css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("theoldreader.com")
{
    
    body
    {
        font-family: "Helvetica Neue", "Roboto", sans-serif;
        color: #50585D !important;
    }
    
    a
    {
        color: #50585D !important;
    }
    
    .nav-list li.nav-header a, .nav-list li.active a
    {
        color: #50585D !important;
        font-weight: normal;
        font-size: 13px;
        text-transform: none;
    }
    
    .nav-list li.active a:not(.i):before, .nav-list li.nav-header.active a:not(.i):before
    {
        border-left: 3px solid #50585D;
    }
    
    .subscribe .btn-primary
    {
        border: none;
        border-radius: 0px;
        box-shadow: none;
    }
    
    .subscribe .icon
    {
        display: none;
    }

    .sidebar-nav .social, .well > ul:nth-child(1) > li:nth-child(3)
    {
        display: none !important;
    }
    
    .nav-list.feeds-list {
        margin-left: 18px;
    }
    
    .static
    {
        max-width: 1224px;
    }
    
    .post
    {
        max-width: 1200px;
    }
    
    .nav-list .badge, .badge.badge-info
    {
        background: none;
        border-radius: 0px;
        text-shadow: none;
        color: #50585D;
        font-size: 11px;
        position:absolute;
        right: 0px;
        line-height: inherit;
    }
    
    .badge.badge-info[style*="rgb(48, 166, 124)"], .badge[style*="rgb(128, 128, 128)"]
    {
        background-color: #50585D !important;
        color: white;
    }
    
    .nav-list strong
    {
        max-width: 80%;
    }
    
    .home-page .relations-info
    {
        display: none;
    }

    .home-page dl dt .label
    {
        background: none;
    }
    
    .dl-horizontal.announcements .label
    {
        color: #50585D;
    }
    
    .page-header h1.username
    {
        overflow: visible;
    }

    .page-header .label, .page-header .label-inverse a
    {
        background: none;
        text-shadow: none;
        color: #50585D;
    }
    
    .post h3
    {
        font-size: 32px;
        padding: 12px;
        line-height: normal;
    }
    
    .post .label
    {
        font-size: inherit;
        background: none;
        text-shadow: none;
        color: #50585D;
        padding: 0px;
    }
    
    .post > div.clearfix > .label:nth-child(1)
    {
        padding-left: 12px;
    }
    
    .well.post:not(.unread)
    {
        border: none;
        background: none;
        box-shadow: none;
    }
    
    .post.unread
    {
        border-left: 4px solid #50585D;
        border-radius: 0px;
    }
    
    .content-body
    {
        padding: 12px;
        font-size: 14px;
    }
    
    .btns
    {
        padding: 12px;
    }
    
    .btn
    {
        background: none;
    }
}
