@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {
    
    .pagesTimelineButtonPagelet {
    background-color: transparent;
    border-bottom: 1px solid transparent;
    min-width: 122px;
    padding: 13px 0px 12px 1px;
    top: 38px;
    z-index: 4;
}
    
    .fbTimelineScrubber {
