    background-attachment:left;
    
}
    
    .hasSmurfbar #blueBarHolder #blueBar:after {
    background-image: url('http://i.imgur.com/RxkbHYc.png');
    background-repeat: repeat-x;
    background-size: auto auto;
    background-position: 0px 0px;
    bottom: -4px;
    content: "";
    height: 4px;
    left: 0px;
    position: absolute;
    right: 0px;
}
.hasSmurfbar #blueBarHolder #blueBar {
    background-image: url('http://i.imgur.com/Kw7w7lf.png');
    background-repeat: repeat-x;
    background-size: auto auto;
    background-position: 0px 0px;
    background-color: #4C66A4;
    border: 0px none;
    box-shadow: none;
    position: relative;
}

