css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("drawi.ru") {
#header {
    background:none;
    margin-top: 3px;   
    border-bottom: 1px solid #555;
}
    #all_cont{
    background: rgba(0,0,0,0.4);
    margin-top: 0;
}
    body{
    background: rgba(0,0,0,0.9);
}
    body {   
    color: rgba(194, 194, 194, 1);
    font-family: "Helvetica Neue",Arial,Helvetica,sans-serif;
}
    h1, h2, h3, h4, h5, h6{color: rgba(194, 194, 194, 1);}
a { 
    color: rgba(143, 191, 230, 1);
       
}
hr{
    border:none;
        background: #333;
    }
.hr{
        border:0px;
    border-bottom:1px solid #333;
        background: #333;
    } 
 .comment{border-bottom:1px solid #333;}   
#content a, #frm a {
color: rgba(143, 191, 230, 1);
    }
.rep_comm{
    background: #222;
    border: 1px solid #333;
}

.filter_pan {
    background: none;
    border: 1px solid #333;
}
.pseudo-select .select {
    color:rgba(143, 191, 230, 1);
    background: none;
        border: 1px solid #333;
}
    .pseudo-select .select:hover{color:#fff;}
.pan_title, .frm_title {
    color: rgba(183, 183, 183, 1);    
}
    .wrap2, .wrap4{border-left: 1px solid #333;}
#menu {
    background:  none;
    margin: -30px 50px;
    border:0px;    
}
#menu a {
    color: rgba(13, 168, 230, 1);
    text-decoration: none;
    padding: 4px;        
}
    #menu li {
    border-right: 0px;}
    #menu a:hover{color:  rgba(0, 228, 255, 1);}
    #menu a:active{background:  #333; }
    LI.m_active{background:  none;}
#panHalls span {
    background:  none;
    border: 1px solid #333;
    padding: 2px 0px 2px;
    border-radius: 0px 0px 5px 5px;
        margin-left:-50px;
}
    .link_hall {
    margin: 0px 3px;
}
    .zoom_text.active > textarea{background:none; color:rgba(13, 168, 230, 1);}
    
#find, #find_btn, #find input {
  background:none;
        color: rgba(100, 111, 200, 1);
        width:200px;
        
}
    #find {border: 1px solid #333;}
    #find_btn{width:50px;
    color:#fff;border: 1px solid #333;}
    
#content .navMenu a {
    color: rgba(186, 11, 251, 1);
    text-decoration: none;
    padding: 10px 150px 10px 7px;
    white-space: nowrap;
    line-height: 25px;
}
    #content .navMenu a:hover {
        color: #fff;
}
    #content .navMenu li:hover {
        background:none;
}
.wid_pic div, .wid_pic_mini div {
    height: 80px;
    width: 125px;
    background: none;
    text-align: center;
    font-size: 14px;
    display: inline-block;
    overflow: hidden;
}
#pic_name_hide {
    display:none;
}
#pic_name {
    color:#ccc;
    text-shadow: 0px 0px 0px ;
    }
#img_wrapp{
        background:none;
        border: 1px solid #333;
    }
#content .medals a {
    color: #ccc;
}
   .p_hidden {background:#333;}
   .user_pics .not_end{background:#303;}
    #shop .myItem{background:#101;}
 #pic_hall .wrap{
        background:none;
        border: 1px solid #333;
    }   
    #img_big, #img_big_view{border: 1px solid #333;}
.tmp_pic{background:none;
    border: 1px solid #333;
}
    .sb_l, .sb_r{background:none; color: #ccc;border: 1px solid #333;}
    
    .pic1 .in, .pic2 .in{background:none; color: #ccc;}
    .d_ar_l, .d_ar_r{background:none; color: #000;}
    .d_silv{color: #000;}
    .inp, .inputs input, .inputs textarea, .full_80, .txt, .inp_small {
        background:none; color: #ccc;border: 1px solid #333;
    }
/*chat*/
#chat_pan{
}
#chat_messages {
    height: 400px;
    overflow-y: auto;
    overflow-x: hidden;
    border: 1px solid #333;
    background: none;
        font-size:150%;
}
    #chat_messages div {
    border-bottom: 0px solid #EEE;
    padding: 2px 2px 1px;
}
textarea.full_80 {
    background: none;
    color: rgba(13, 168, 230, 1);
    font-size:200%;
        border: 1px solid #555;
}
.to_me {
    background: none ;
    font-weight: bold;
    color: #fff;
    font-size:140%;
}
.btn, .btn_red, .btn_blue {
    box-shadow: 0px 0px 4px #7E7E7E;
    border-radius: 5px;
    background: none;
    color: #fff;
    opacity: 0.9;
}
    
#totop{background: none;}
    #sys_mess .s_m{background: none;}
#footer div {
 display: none;
}
/*icons*/
    [class^="icon-"], [class*=" icon-"]{
        background-image: url('http://fc05.deviantart.net/fs70/f/2014/100/2/5/glyphicons_halflings_by_danillius-d7dwmyi.png');
    }
    
/*draw2*/
    .cToolbar{background:#222; width:102px;}    
    div.cLr{background:#000; border:1px solid #555;}
    .cLr.active{background:#333;}
    #frm{background:#000; border-radius:5px;}
    .cBrush{background:#aaa; color:#000;}
    .cBrushGroup{background:#000;}
    .cToolbar .cPopup, .cColorPicker{background:#000;}
    .ui-widget-content{ background:none; border:1px solid #555;}
    .ui-slider-horizontal {    height: 0.3em;}
    .ui-slider .ui-slider-handle {
    width: 1em;
    height: 1em;}
  .cColorPicker {
    left: 70px;  
}
    .cBtn {width:100px;}
    .ui-state-default, .ui-widget-content .ui-state-default{background:#000; border:1px solid #ccc;}
}
