/*
Youtube Shadow
by Nonprofit Prophet
primedesigning.com/styles
*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(youtube.com) {
/*YOU ONLY HAVE TO EDIT THIS SECTION TO CHANGE BACKGROUND, BORDER, AND SHADOW COLORS*/
#watch-headline-title, #watch7-sidebar, #player-api, #yt-masthead-container, #appbar-guide-menu, .branded-page-v2-header, .yt-card, #appbar-content, #footer-container, #watch7-content, #watch7-user-header, .watch-card {
    border:2px solid /*[[border]]*/ !important;
    box-shadow:0px 0px 10px 0px /*[[shadow]]*/ !important;
}

body, #watch7-container, #watch7-headline {
    background:/*[[background]]*/ !important;
}

/*VIDEO TITLE*/
#watch-headline-title {
    background:#fff !important;
    color:#1b1b1b !important;
}

/*SHADOWS ONLY ON TOP AND SIDES*/
#watch7-user-header {
    box-shadow: 0px 12px 0px 0px #fff, 0px 0px 10px 0px /*[[shadow]]*/ !important;
}

/*SHADOWS ONLY ON SIDES*/
#watch7-action-buttons, #watch7-action-panels{
    box-shadow: 0px 12px 0px 0px #fff, 0px -12px 0px 0px #fff, 0px 0px 10px 0px /*[[shadow]]*/ !important;
}

/*SHADOWS ONLY ON BOTTOM AND SIDES*/
#watch-discussion  {
    box-shadow: 0px -12px 0px 0px #fff, 0px 0px 10px 0px /*[[shadow]]*/ !important;
}

/*SHADOWS ONLY ON RIGHT SIDE*/
#appbar-guide-menu  {
    box-shadow: 0px -12px 0px 0px #fff, 0px 8px 10px 0px /*[[shadow]]*/ !important;
}
/*END EDIT SECTION*/


/*SEARCH BOX*/
#masthead-search-term {
    font-size:22px !important;
    padding-top:1px !important;
    color:#444 !important;
}


/*POSITION FIXES - DON'T EDIT UNLESS YOU KNOW WHAT YOU'RE DOING!*/
#watch7-headline {
    padding-bottom:4px !important;
    margin-left:-2px !important;
    margin-right:2px !important;
    padding-left:0px !important;
    border:0px solid !important;
}

div.yt-uix-button-panel {
    width:644px !important;
}

#watch7-action-panels {
    width:639px !important;
}

#watch-headline-title {
    text-align:center !important;
    margin:0px !important;
    padding:0px !important;
    padding-left:3px !important;
    padding-bottom:2px !important;
    margin-right:0px !important;
    margin-top:-16px !important;
    margin-bottom:5px !important;
    height:initial !important;
    width:637px !important;
}

#watch-headline-title span, #watch-headline-title strong {
    margin:0px !important;
    color:inherit !important;
}

#watch-headline-title strong {
    margin-right:-5px !important;
}

#player-api {
    width:640px !important;
    height:390px !important;
    margin-bottom:-1px !important;
	margin-top:0px !important;
}

#watch7-sidebar {
    margin-top:-393px !important;
    margin-left:653px !important;
    padding-bottom:10px !important;
}

#watch7-content {
    width:640px !important;
    border-top:0px !important;
    box-shadow:0px 0px 0px 0px #000 !important;
}

.playlist-videos-list {
    max-height:294px !important;
    border:0px !important;
}

#masthead-appbar-container {
    margin-top:-2px !important;
}

#yt-masthead {
    margin-top:-2px !important;
    margin-bottom:2px !important;
}


/*BORDER FIXES - DON'T EDIT UNLESS YOU KNOW WHAT YOU'RE DOING!*/
#watch7-user-header {
    border-left:0px !important;
    border-right:0px !important;
    padding-top:12px !important;
    margin-right:4px !important;
}

#watch7-action-buttons, #watch7-action-panels {
    border-top:0px !important;
    border-bottom:0px !important;
    margin-right:4px !important;
}

#watch-discussion {
    border-top:0px !important;
}

#appbar-guide-menu {
    border-left:0px !important;
    border-bottom:0px !important;
    margin-top:49px !important;
    margin-right:-1px !important;
}

#yt-masthead-container {
    border-top:0px !important;
    border-left:0px !important;
    border-right:0px !important;
    z-index:9999 !important;
}

.branded-page-v2-header {
    border-top:0px !important;
}

#appbar-content {
    border-left:0px !important;
    border-right:0px !important;
    border-top:0px !important;
    height:39px !important;
    box-shadow:0px 0px 0px 0px #000 !important;
}

#footer-container {
    border-right:0px !important;
    border-left:0px !important;
    border-bottom:0px !important;
    margin-bottom:-16px !important;
}

.watch-card-collage {
    border:2px solid #000 !important;
    border-left:0px !important;
    border-right:0px !important;
}


/*SHADOW FIXES - DON'T EDIT UNLESS YOU KNOW WHAT YOU'RE DOING*/ 
#content {
    border:0px !important;
    box-shadow:0px 0px 0px 0px #000 !important;
}


/*MISC FIXES - DON'T EDIT UNLESS YOU KNOW WHAT YOU'RE DOING*/
#search-secondary-col-contents {
    visibility:hidden !important;
}

.watch-card {
    visibility:visible !important;
    width:300px !important;
}

}
