css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("volafile.io") {

#overlay.visible {
    background: rgb(0,2,5);
}

#room_name, #room_name_container{

    text-align: center;

    background: rgb(0,10,25);

    color: rgb(200,200,200);

}

#header_row1.header_row {

    background: rgb(0,10,25);

}

.fa {
  
    color: rgb(200,200,200)!important;
    
}

.chat_message {

    background: rgb(15,20,30);

    color: rgb(200,200,200);

}

.chat_message.user .username {

    color: rgb(120,140,200);

}

.chat_message.admin .username {

    color: rgb(200,120,140);

}

.chat_message.admin {

    background: rgb(18,22,30);

}

.chat_message.rank .username {

    color: rgb(80,100,180);

}

.chat_message .username {

    color: rgb(180,190,200);

}

.chat_message.highlight, .chat_message.highlight.admin {

    background: rgb(25,35,50);

    border-color: rgb(200,120,140);

    border-width: 4px    

}

.chat_file {

    border-radius: 1px;

    background: rgb(30,40,50);
  
    border-color: rgb(40,52,78);
  
    color: rgb(200,200,200);

}

.nano > .pane > .slider {

    border-radius: 1px;

    background: rgb(50,70,90);

}

.nano > .pane > .slider:hover {

    background: rgb(50,70,100)

}

.nano:hover > .pane, .pane.active, .pane.flashed {

    background: rgb(20,30,50);

    opacity: 1

}

.nano > .pane.active > .slider {

    background: rgb(120,140,170);

}

#chat_hbar, #header_row2.header_row {

    background: rgb(25,30,40);

    color: rgb(200,200,200);

}

#chat_name, #chat_input {

    background: rgb(10,15,25);

    color: rgb(200,200,200);

}

a:hover {

    color: rgb(120,140,200);

}

textarea.defaultValue {

    color: rgb(200,200,200);

}

#search.defaultValue {

    background: rgb(40,50,70);

    color: white;

}

input#search {

    background: rgb(45,55,75);

}

.toggle > i {
    
    color: rgb(200,200,200);
    
}

.toggle.enabled > i {

    color: rgb(120,140,200)!important;
    
}

.button, input[type="submit"] {

    background: rgb(25,30,40);

    color: rgb(120,140,200);

    margin-right: 10px;

}

#uploadButton {

    border-radius: 0px;

}

#uploadButton:hover {

    color: rgb(140,160,200);

    background: rgb(40,45,55)

}

#uploadButton:active {

    background: rgb(40,45,55);

    color: rgb(200,120,140)

}

#filter_reminder {

    background: rgb(30,50,70);

}

#filter_reminder:hover {

    background: rgb(40,60,80);

}

.file_uploading {

    background: rgb(40,60,80)!important;
    
}

#file_list.uneven .filelist_file:nth-child(2n+1) {
    background: rgb(15,20,30);
    color: rgb(200,200,200);
}

#file_list.even .filelist_file:nth-child(2n) {

    background: rgb(15,20,30);
    
    color: rgb(200,200,200);
    
}

.filelist_file {

    background: rgb(0,10,15);
    
    color: rgb(200,200,200);
    
}

.file_icon.filetype.music_playing {

    color: rgb(100,120,180);
    
}

.gallery_button {
  
    border-radius: 1px;
  
    border-color: rgb(40,52,76);
  
    color: rgb(200,200,200);
    
}

.ui_frame {
  
    background: rgb(0,10,15);
    
    border-color: rgb(50,70,90);
    
    border-radius: 1px;
    
}

a.file_name {
  
  color: rgb(200,200,200);
  
}

a.file_name:visited:hover {
  
  color: rgb(100,120,180);
  
}

#a:-webkit-any-link:visited {
  
  color: rgb(200,120,140);
  
}

a:hover {
  
  color: rgb(100,120,180);
  
}

.file_control_icon.clickable {
  
      color: rgb(200,200,200);
      
}
    
.file_control_icon.clickable:hover {
  
      color: rgb(180,180,180);
            
}
          
.file_icon {
        
      color: rgb(200,200,200);
              
}

html {

    background: rgb(0,10,15);
    
}

#main_logo {

    color: rgb(60,80,130);
    
}

#main_logo_suffix {

    color: rgb(100,120,180);
    
}

.button:hover {

    background: rgb(25,35,45);
    
}

.Button:active {

    background: rgb(40,45,60)!important;
    
}

pre {
  
  color: rgb(200,200,200);
  
}

/* fauxpas additions */

#main_discover_button {

    background: rgb(0,10,25);
    
    color: rgb(200,200,200);
    
}

#main_discover_button a:visited {

    color: rgb(200,200,200);
    
}

#main_discover_button a:visited:active {

    color: rgb(200,120,140);
    
}

#gallery_title {
  
    line-height: 2em;
    
}

#gallery_image_row_container {
  
    top: 2.5em;
  	bottom: 6.25em;
  	right: 3em;
  	left: 3em;
  
}

.gallery_button {
  
    background: 0 0;
    line-height: 1.05em;
    border-radius: 0em;
    border-color: rgb(120,140,200);
      
}
  
.fa_gallery_button {
  
    cursor: pointer;
    font-size: 2em;
    padding: .3em
        
}

#header_row_container a i.green-icon {

    color: rgb(200,200,200);

}

#header_row_container a:hover i.green-icon {

    color: rgb(120,140,200);

}

#header_row_container a:active i.green-icon {

    color: #939393;

}

a.file_name:visited {
  
    color: #939393;
    
}

a:active {

	color: rgb(200,120,140);

}

#radio_container {

	background-color:transparent;

}

#radio_current {
  
	background-color:rgb(15,20,30); 
  
}
}
