/* ==UserStyle==
@name Persian Facebook
@namespace arefi.ir
@description Persian Facebook by Ashkan Arefi
@updateURL http://arefi.ir/ashkan/fb/arefi_fb_v1.user.css
@downloadURL http://arefi.ir/ashkan/fb/arefi_fb_v1.user.css
@version 1.2
==/UserStyle== */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("facebook.com"),
url-prefix(javascript:) {    
    * { font-family: Tahoma !important; font-size: 13px !important; direction: rtl !important; }
    .linkWrap { font-size: 11px !important; }
    .linkWrap span { font-size: 11px !important; }
    .UFILikeLink, .uiLinkButtonInput, .share_action_link { font-size: 11px !important; }
    .countValue { font-size: 9px !important; }    
    .ego_section a { font-size: 9px !important; }
    .ego_section div { font-size: 9px !important; }
    .ego_section span { font-size: 9px !important; }
    .ego_section button { font-size: 9px !important; }
    .home_right_column, #rightCol, .UIStandardFrame_SidebarAds { opacity:0.4 !important; }
    .home_right_column:hover, #rightCol:hover, .UIStandardFrame_SidebarAds:hover { opacity:0.8 !important; }    
    #rightCol { width:300px !important; }
    ._li, #contentCol { background-color:#fcfbfa !important; }
    #blueBar { background-color:#5490cd !important; background-image:none !important; }        
    #globalContainer, #pageHead { width: 1300px !important; } 
    #headerArea { width: 740px !important; }  
    #leftCol { width: 260px !important; }        
    .noCount, .hasCount { max-width: 230px !important; }         
    div#pageHead { background-position:365px; background-repeat:no-repeat;
        background-image:url(http://arefi.ir/ashkan/fb/copyright.png) !important; }      	
    #contentArea ._4-u2 { border-width:1px !important; border-radius:10px !important;
    margin-right:50px !important; width:600px !important; 
    box-shadow: 0px 0px 8px 3px #CCC !important; margin-bottom:30px !important; }    
    ._5pbx  { line-height:30px !important; }
    .UFIBlingBoxCommentIcon, .UFIBlingBoxLikeIcon { margin-right:20px !important; }
    .UFIBlingBoxText { font-size: 12px !important; }
    #fbMessagesJewel { margin-right: 20px !important; margin-left: 20px !important; }
    #pageLogo { display: none !important; }
    #navSearch { float: left !important; width: 280px !important; margin-left: 10px !important; }    
    div#headNav ._ohf { float: right !important; }    
    #jewelContainer { margin-right: 15px !important; margin-left: 35px !important; }    
    div.userContentWrapper div.mtm { margin-right: 30px !important;  }    
    .rhcFooterWrap a, .rhcFooterWrap span { font-size: 10px !important;  }  
    #jewelContainer .fbJewelFlyout { left: -180px !important;  } 
    #jewelContainer .beeperNub { left: 185px !important;  }     
    #pagelet_web_messenger { width: 900px !important;  }        
    ._2nb { width: 580px !important;  }         
    ._2nc { width: 580px !important; }          
    .UFIComment { margin:3px 35px 3px 0px !important; line-height:18px !important; 
    width: 300px !important; border:solid #DDD 1px !important; 
    border-radius:10px !important; background-color:#f1f1f1 !important; }
    .fbPhotosSnowliftFeedback .UFIComment { margin:3px 0px 3px 0px !important; width: 250px !important; }
    .fbPhotosSnowliftFeedback .UFIReplyList .UFIComment { margin:3px 0px 3px 0px !important; width: 210px !important; }    
    .UFIReplyList .UFIRow { width:240px !important; }
    .UFIReplyList .UFIRow span { font-size:11px !important; }
    .UFIReplyList { background-color:#fcfbfa !important; }
    a.jewelButton { background-image: url('http://arefi.ir/ashkan/fb/fb_icons.png') !important; }
    #leftCol { opacity:0.7 !important; } #leftCol:hover { opacity:0.9 !important; }
    div.userContent, div._wk { padding:30px 60px 30px 60px !important;  
    border-top:solid #DDD 1px !important; text-align:justify !important; }
    img._s0 { border-radius:5px !important; }
    .UFIList { padding:20px !important;  border-top:solid #DDD 1px !important; }
    .UFIRow { padding:10px !important;  border-top:solid #DDD 1px !important; }    
    #pagelet_composer { padding:10px !important; }
    #pagelet_composer .mtm {  background-color:#f3f2f1 !important; }
    ._4-u2 {  background-color:#fffefd !important; }    
    #headerArea ._46-i {  width:740px !important; left:45px !important; }       
    #headerArea .uiScaledImageContainer:not(.photo) .scaledImageFitWidth {  width:740px !important; }                    
    .commentable_item {  margin:40px 20px 20px 0px !important; }       
    .fbTimelineComposerCapsule {  margin:70px 0px 30px 0px !important; }       
    a.see_more_link,a.see_more_link:link {  padding:0px 20px 0px 0px !important; 
    font-weight:bold !important; font-size:16px !important;  }       
    .mtm .pbm {  width:470px !important; right:30px !important; }           
    .userContentWrapper ._5x46 {  padding:10px !important; background-color:#f3f2f1 !important; 
    border:solid #DDD 1px !important; border-radius:10px !important; }
    div._4r_y { margin:0px 0px 0px 10px !important; }
}
