css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pcgamer.com") {

  #main, #adSkinLeft, #adSkinRight, body, body.review_page .center-section.narrow, body.review_page .review-verdict, #adSkinFull, .newsletter-signup, .newsletter-signup *, .popular-box, .impact .listingResult {
    background-color: black !important;
    background-image: none !important;
  }
  
  .page-wrapper .page-inner #container #page-content {
    background-color: #242424;    
  }
  
  a, .tags a { background-color: #444; }
  
  #stream .news div h3 { color: white; }

  .preserve-3d.no-touch .extrude .inner { background-color: black; }
  
  .article_box_wrap .byline { background-color: black; }
  
  #stream .evergreen { background-color: black; }
  
  h1, h2, h3, h4, h5, h6 { color: #9E9E9E; }
  
  p { color: #9E9E9E; }
  
  .shareSocialArticle .share-toggle, .shareSocialArticle .secondary-share-toggle { background-color: #383838; }
  
  .hawk-top-deals h2 { color: #9E9E9E; }
  .hawk-top-deals p { color: #9E9E9E; }
  
  #gallery_wrap { background-color: black;  }
  
  body.gallery_page .gallery_title, body.review_page .gallery_title { color: #9E9E9E; }
  
  .gallery_slideshow_counter .next { background-color: transparent; }
  .gallery_slideshow_counter .prev { background-color: transparent; }
  
  li, .pagecomponent h4 { color: #9e9e9e; }
  
  .fancybox, .fancy-box { background-color: black; }
}
