css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("facebook.com")

{

/* Bluebar, top bar */

	/* The bar itself */

		#blueBar

		{

			box-shadow:

				inset 0 -10px 15px -5px rgba(0, 0, 0, 0.3),

				inset 0 10px 15px -5px rgba(0, 0, 0, 0.3),

				inset 0 22px 10px rgba(255, 255, 255, 0.0)

					!important;

				

			border-bottom:

				1px solid #000

					!important;

		}



		#blueBar:after /* Removes that 1px underline above the bluebar */

		{

			background-image:

				none

					!important;

		}



		._56lq, ._55bh:after /* Small spacing between the nav buttons, small border at the left of the menu-pulldown */

		{

			display:

				none

					!important;

		}



	/* Friend requests | Messages | Notifications objects effects on hover */

	.objectListItem, .messagesContent, .anchorContainer a

	{

		transition:

			0.3s;

	}



	.objectListItem:hover, .messagesContent:hover, .anchorContainer a:hover

	{

		box-shadow:

			inset 0 0 0 1px rgba(0, 0, 255, 0.5),

			inset 0 0 5px rgba(0, 0, 0, 0.3)

				!important;

		

		background:

			rgba(0, 0, 0, 0.04);

	}

		

	/* Bar nav buttons */

		/* Nav links (home, post, menu-pulldown) */



		.headerTinymanPhoto, .fbxWelcomeBoxBlock .fbxWelcomeBoxImg /* The picture in the "Name" button, the picture in the left bar */

		{

			border-radius:

				100px

					!important;

		

			box-shadow:

				0 0 0 1px #000;

		}

		



		._5bfg #pageNav._5bfj .tinyman .navLink:after, ._5bfg #pageNav._5bfj .tinyman .navLink:hover:after, .litestandClassicWelcomeBox .fbxWelcomeBoxBlock:after /* A border around the profile image icon, this removes it */

		{

			background:

				none

					!important;



			border:

				none

					!important;

		}



		#pageNav .navLink

		{

			height:

				30px

					!important;

			

			padding-right:

				8px

					!important;

			

			margin-top:

				-12px

					!important;

			

			transition:

				0.2s;

			

			padding-top:

				10px

					!important;

			

			background:

				#526da4

					!important;

			

			box-shadow:

				0 0 0 1px #000,

				inset 0 20px 15px -15px rgba(0, 0, 0, 0.6),

				inset 0 -20px 15px -15px rgba(0, 0, 0, 0.4),

				inset 0 0 0 1px rgba(255, 255, 255, 0.3);

			

			border-radius:

				0 0 8px 8px

					!important;

			

			z-index:

				1;

			

			margin-right: /* This stops the nav buttons from moving left when hovered */

				0px

					!important;

		}



		._59fc /* Privacy button div, for some reason .navLink doesn't apply the width. This forces it to be applied */

		{

			width:

				30px

					!important;

		}



		#privacyFlyoutLabel /* Privacy button icon, this moves it a bit to the right, so it gets centered into the button */

		{

			margin-left:

				10px

					!important;

		}



		#pageLogo /* F button effects */

		{

			height:

				36px

					!important;



			width:

				36px

					!important;

			

			margin-top:

				-8px

					!important;

			

			transition:

				0.2s;

			

			padding-top:

				10px

					!important;

			

			background:

				#526da4

					!important;

			

			box-shadow:

				0 0 0 1px #000,

				inset 0 25px 15px -15px rgba(0, 0, 0, 0.6),

				inset 0 -20px 15px -15px rgba(0, 0, 0, 0.4),

				inset 0 0 0 1px rgba(255, 255, 255, 0.3);

			

			border-radius:

				0 0 8px 8px

					!important;

		}



		

		/* Jewel Buttons, this sets their initial position */

		#fbRequestsJewel, #fbMessagesJewel, #fbNotificationsJewel

		{

			transition:

				0.2s;

			

			margin-top:

				-7px

					!important;

			

			padding-right:

				3px;

			

			padding-left:

				3px;

			

			box-shadow:

				0 0 0 1px #000;

			

			height:

				30px 

					!important;

			

			padding-top:

				10px

					!important;

			

			background:

				#526da4

					!important;

			

			box-shadow:

				0 0 0 1px #000,

				inset 0 20px 15px -15px rgba(0, 0, 0, 0.6),

				inset 0 -20px 15px -15px rgba(0, 0, 0, 0.4),

				inset 0 0 0 1px rgba(255, 255, 255, 0.3);

			

			border-radius:

				0 0 8px 8px;

			

			margin-bottom:

				10px

					!important;

			

			margin-left:

				5px;

			

			margin-right:

				5px;

		}

		

		/* This changes the position of the menu that pops up when you click a jewel, so it fits the button when it is pulled down, a quick-fix */

		.jewelFlyout

		{

			margin-top:

				15px;

		}



		#navAccount .menuPulldown /* This to fix the location of the button icon */

		{

			margin-top:

				15px

					!important;

			

			margin-left:

				5px

					!important;

			

			margin-right:

				4px

					!important;

		}



		#navAccountLink /* This to fix the height of the button to match the other nav buttons' alignment, brazilian "gambiarra" */

		{

			top:

				-4px;

				

			box-shadow:

				0 0 0 1px #000,

				inset 0 25px 15px -15px rgba(0, 0, 0, 0.6),

				inset 0 -20px 15px -15px rgba(0, 0, 0, 0.4),

				inset 0 0 0 1px rgba(255, 255, 255, 0.3)

					!important;

		}



		#navAccountLink /* There's a border in the nav account button, this removes it */

		{

			border:

				none

					!important;

		}



		#pageNav .navLink:hover, #fbRequestsJewel:hover, #fbMessagesJewel:hover, #fbNotificationsJewel:hover, #pageLogo:hover /* This makes the nav buttons go down when hovered */

		{

			padding-top:

				20px

					!important;

					

			background:

				#526dc4

					!important;

		}



		#pageNav .navLink:active, #fbRequestsJewel:active, #fbMessagesJewel:active, #fbNotificationsJewel:active, #pageLogo:active /* This adds an active effect to the nav buttons */

		{

			background:

				#203ba2

					!important;

		}

/* Here ends the bar itself */













/* News feed stuff */

		.uiTypeahead .uiTextareaAutogrow, #composerCityTagger, ._1dsr, ._9lb, #pagelet_composer .uiSelectorButton /* "Post" screen transitions */

		{

			transition:

				0.2s

					!important;

		}



		#pagelet_composer .uiToggle .uiSelectorButton /* In the news feed, there's the "who can see" button, this blocks the transition used above, avoiding graphic glitches */

		{

			transition:

				0s

					!important;

		}

		

		#pagelet_composer .uiTextareaAutogrow:hover, #pagelet_composer .uiTextareaAutogrow:focus /* Composer textarea onhover/onfocus effects */

		{

			box-shadow:

				inset 0 0 0 1px rgba(0, 0, 255, 0.5),

				inset 3px 3px 3px rgba(0, 0, 0, 0.3)

					!important;

		}



		#composerCityTagger:hover, ._1dsr:hover /* "Post" screen buttons effect on hover */

		{

			box-shadow:

				inset 0 0 1px #000;

			

			margin-top:

				-2px !important;

			

			height:

				45px

					!important;

		}

/* Here ends the news feed stuff */





















/* Right chatbar stuff */



	.tickerStoryClickable ._54ru

	{

		box-shadow:

			0 0 0 1px #000;



		border-radius:

			100px;



		transition:

			0.3s;

	}





	.tickerStoryClickable:hover ._54ru

	{

		border-radius:

			4px;



		box-shadow:

			0 0 0 2px #000;

	}





	.tickerStoryClickable:hover

	{

		box-shadow:

			inset 0 0 0 1px rgba(0, 0, 255, 0.5),

			inset 0 0 5px rgba(0, 0, 0, 0.3)

				!important;

		

		background:

			rgba(0, 0, 0, 0.04);

	}



	.uiScrollableAreaContent ._55ln, #sidebar_navigation_top ._4g5p, ._4gb_ /* This adds the extra spacing I need to add "borders" on each chat div */

	{

		margin-top:

			1px;

		

		padding-top:

			2px

				!important;

		

		padding-bottom:

			2px

				!important;

		

		transition:

			0.2s;

	}





	.uiScrollableAreaContent ._55ln:hover, #sidebar_navigation_top a:hover /* This, is the animation of the chat divs when hovered */

	{

		box-shadow:

			0 0 0 1px #000,

			0 0 0 2px rgba(255, 255, 255, 0.3)

				!important;

	}

/* Here ends the right chatbar stuff */





















/* Round profile pictures */

	/* Feed page round images */

	._5pb8 .img, ._5v6e .ego_unit ._s0

	{

		border-radius:

			100px;

		

		transition:

			0.3s;

		

		box-shadow:

			0 0 0 1px #000;

	}



	._5v9u:after /* Removes a border around the profile picture in the news feed */

	{

		border:

			none

				!important;

	}



	._5pb8:hover .img, ._5v6e .ego_unit:hover ._s0 /* Effects on the profile pictures in the right action bar */

	{

		border-radius:

			4px

				!important;

		

		box-shadow:

			0 0 0 2px #000;

	}



	._5v6e .ego_unit /* Effects when hovering the right action bar */

	{

		border-radius:

			4px;



		width:

			260px

				!important;



		padding:

			5px

				!important;



		margin-left:

			-8px

				!important;

	}



	._5v6e .ego_unit:hover

	{

		box-shadow: 0 0 0 1px rgba(0, 0, 0, 0.3);



		background: rgba(0, 0, 0, 0.1);

	}

	/* Feed round profile picture ends */



	/* Chatbar round profile pictures */



	._55lt .img, ._521h ._4-k7 .img

	{

		box-shadow:

			0 0 0 1px #000;



		border-radius:

			100px;



		transition:

			0.3s;

	}



	._5pr2 ._56p9:after /* Removes the border around the profile picture on the chatbar */

	{

		border:

			none

			!important;

	}



	._55ln:hover ._55lt .img /* Chatbar profile picture effects */

	{

		border-radius:

			3px

				!important;

		

		box-shadow:

			0 0 0 1px

				#666;

	}



	#pagelet_canvas_nav_content, #apps_gripper /* Removes adds on the already small chatbar. */

	{

		display:

			none

				!important;

	}



/* Left bar stuff, still testing */



	#pagelet_navigation /* The left bar has no background/borders. This adds them (looks better :D) */

	{

		background:

			#FFF;



		padding:

			5px;



		margin-top:

			15px;



		margin-left:

			-15px;



		border-radius:

			3px;



		box-shadow:

			0 0 0 1px rgba(0, 0, 0, 0.05);

	}



	#pagelet_welcome_box /* Your name/profile picture on the left "bar" has no background, this adds them */

	{

		background:

			#FFF;



		margin-left:

			-15px;



		padding:

			8px;



		border-radius:

			3px;



		box-shadow:

			0 0 0 1px rgba(0, 0, 0, 0.05);

	}





	.sideNavItem

	{

		margin-left:

			0px

				!important;

		

		transition:

			0.3s;

		

		border:

			1px solid rgba(0, 0, 0, 0);

	}



	.sideNavItem:hover /* Effects when hovering the elements on the left bar, applies to buttons on the left of the element */

	{

		background:

			rgba(0, 0, 0, 0.05);



		border-radius:

			3px;



		border:

			1px solid rgba(0, 0, 0, 0.2);



		transition:

			0.2s;

	}



	.sideNavItem .item:hover /* Effects when hovering the elements on the left bar, applies to the elements itself */

	{

		background:

			rgba(0, 0, 0, 0.025)

				!important;

	}

}
