css:
@namespace "http://www.w3.org/1999/xhtml";
@-moz-document domain("reddit.com") { 
    body, body>.content, .md table {
     background: #121317!important; color: #A3DFD3!important;
    }
    a {
     color: #00FCFF!important; text-shadow: none!important;
    }
    a:hover {
     color: #ffffff!important
    }
    a:visited {
      color: #ffffff
    }
    h2 {
     color: #A3DFD3!important
    }
    .link.last-clicked {
     border-color: #30659b!important; border-color: #3d4044!important; opacity: .6!important;
    }
    #header {
     background-color: #121317!important; border-color: #121317!important;
    }
    #header-bottom-right {
     background-color: #121317!important
    }
    #header-bottom-right span {
     color: #bfc5ca!important
    }
    #header-bottom-right a {
     color: #ffffff!important
    }
    .titlebox .usertext-body .md ol {
     border-color: #121317!important
    }
    .tabmenu li a {
     background: #121317!important; border-radius: 4px 4px 0 0!important; font-size: 11px!important;
    }
    .tabmenu li.selected a {
     border-color: #202328!important; background: #74787c!important; color: #202328!important; box-shadow: 0 1px 0 #898e92 inset;
    }
    #userbarToggle {
     background: #202328!important; border-color: #3D4044!important; color: #3D4044!important;
    }
    .pagename a {
     color: #ffffff!important
    }
    .thumbnail {
     max-width: 60px!important; max-height: 60px!important; border: #3e4145 1px solid!important; border-radius: 4px!important; background-color: #272b30!important;
    }
    .panestack-title, .footer, .footer .col {
     border-color: #3d4044!important
    }
    .link {
     margin: 14px 0!important
    }
    .linkinfo {
     background-color: #202328!important; border-color: #656b6c!important; color: #646d75!important; border-radius: 6px!important; box-shadow: 0 3px 6px #202328!important; text-align: center!important;
    }
    .linkinfo .score .number, .titlebox .karma {
     color: #ffffff!important
    }
    .linkinfo .score .word {
     color: #93a0ac!important
    }
    .linkinfo .upvotes {
     color: #c63b31!important
    }
    .title {
     color: #949495!important; font-family: "Helvetica"!important; font-weight: 100!important; font-size: 16px!important; line-height: 18px!important;
    }
    .odd {
     margin: 0!important; padding: 5px 0!important;
    }
    .nsfw-stamp acronym {
     color: #d57852!important; font-size: 8px!important; background-color: #202328!important; border-color: #3d4044!important;
    }
    .tagline {
     color: #8D8D8D!important
    }
    .tagline a {
     color: #7d9f9b!important
    }
    .domain a {
     color: #8eafc6!important
    }
    .entry .buttons li a {
     color: #656b71!important
    }
    .link .usertext .md {
     background-color: #121317!important; border-color: #3a3c41!important; box-shadow: 0 4px 6px 1px rgba(0,0,0,0.2) inset;
    }
    .arrow {
     opacity: .15!important
    }
    .arrow.upmod, .arrow.downmod {
     opacity: .8!important
    }
    .expando-button {
     opacity: .5!important; background-color: #272b30!important; border-color: #fff!important;
    }
    .newComments {
     color: #bfc82f!important; font-weight: normal!important;
    }
    .link .score {
     color: #757c82!important; font-weight: normal!important;
    }
    .link .score.likes {
     color: #d4a18d!important
    }
    .dislikes div.score.dislikes {
     color: #8382d7!important
    }
    .adzerk-vote{
        width:0px;
        height:0px;
    }
    .link .rank {
     color: #3e4145!important
    }
    .organic-listing .link, .organic-listing .link.promotedlink {
     border-radius: 6px!important; border-color: #121317!important; background-color: #121317!important;
    }
    .organic-listing {
     border: none!important
    }
    .titlebox h1 {
     color: #bbbfb3!important
    }
    h1.redditname a {
     color: #ffffff!important; font-size: 30px!important;
    }
    .titlebox .bottom {
     border-color: #656b6c!important
    }
    .fancy-toggle-button .active {
     color: #ffffff!important; font-weight: normal!important;
    }
    .side {
     background-color: #121317!important; padding: 6px!important; border-radius: 6px!important; margin-top: 6px!important; /*box-shadow: -4px 10px 4px rgba(0,0,0,0.1)!important;*/
    }
    .titlebox form.toggle, .leavemoderator {
     background-color: #121317!important; color: #ffffff!important; padding: 4px!important;
    }
    .sidebox div.spacer {
     background-color: #121317!important; border-radius: 6px!important; /*box-shadow: 0 1px 0 #898e92 inset;*/
    }
    .sidebox div.subtitle {
     color: #d9dddd!important
    }
    .nub {
     display: none!important
    }
    .searchpane {
        background-color: #121317!important;
    }
    .searchfacets {
        background-color: #121317!important;
        box-shadow:0 0 0 0;
    }
    .facet.title.word{
        color: #06989A!important;
    }
    .facet.count.number{
        color: #06989A!important;
    }
    .morelink, .nextprev a, .next-suggestions a {
     background: #121317!important; border-color: #222222!important; box-shadow: 0 1px 0 #3a3c41 inset,0 2px 3px #202328!important;
    }
    .morelink:hover, .nextprev a:hover, .next-suggestions a:hover {
     background-color: #000000!important; box-shadow: 0 1px 0 #686b6e inset,0 2px 3px #202328!important;
    }
    .morelink a {
     color: #00FCFF!important; letter-spacing: 1px!important; font-size: 14px!important; font-weight: normal!important;
    }
    .nextprev a, .next-suggestions a {
     color: #5e5d5d!important; padding: 3px 10px!important; display: inline-block!important;
    }
    .nextprev a:hover, .next-suggestions a:hover {
     color: #ffffff!important
    }
    .next-suggestions {
     float: right!important
    }
    #search input[type="text"] {
     font-size: 13px!important; padding: 4px 0 4px 4px!important; border-color: #A3DFD3!important; border-radius: 6px!important; background-color: #222222!important; color: #A3DFD3!important;
    }
    .sidecontentbox .title h1 {
     color: #b3bfb3!important; font-family: Helvetica!important; text-transform: lowercase!important; font-size: 14px!important; letter-spacing: 1px!important;
    }
    .sidecontentbox .content {
     border-color: #3e4145!important; background-color: #202328!important;
    }
    .sidecontentbox a {
     color: #757c82!important
    }
    .sidecontentbox small {
     color: #3e4145!important
    }
    .sidecontentbox small a {
     color: #bbbfb9!important
    }
    .sidecontentbox a.helplink {
     padding: 12px 4px!important
    }
    .account-activity-box a {
     color: #b5bfb9!important
    }
    .giftgold {
     font-size: 11px!important
    }
    .giftgold a {
     color: #fde379!important; font-weight: normal!important;
    }
    .trophy-name {
     color: #bfc5ca!important
    }
    #RESStaticShortcuts a {
     color: #ffffff!important
    }
    #RESStaticShortcuts a:hover {
     color: #30659b!important
    }
    #sr-header-area, #RESShortcutsEditContainer {
     background-color: #121317!important; color: #808080!important;
    }
    #RESShortcutsRight, #RESShortcutsLeft, #RESShortcutsAdd, #RESShortcutsTrash {
     background: #74787c!important; color: #fff!important;
    }
    #DashboardLink a {
     color: #ffffff!important
    }
    .RES-keyNav-activeElement, .commentarea .RES-keyNav-activeElement .md, .commentarea .RES-keyNav-activeElement.entry .noncollapsed {
     background: #3d4044!important
    }
    .RES-keyNav-activeElement {
     outline: 1px solid #898e92!important; padding: 4px!important;
    }
    .RESSubscriptionButton {
     background: #3D4044!important; color: #979CA1!important;
    }
    .usertext.grayed .usertext-body {
     background: #3d4044!important; color: #979c98!important;
    }
    .link.odd .entry {
     border: none!important
    }
    .comment .child, .menuarea {
     border-color: #3d4044!important
    }
    .comment, .content .details {
     margin-left: 0!important
    }
    .wikipage h1, .wikipage h2, .wikipage h3, .wikipage h4 {
     color: #ffffee!important
    }
    .wikipage table td, .wikipage table th {
     border-color: #3d4044!important
    }
    .md.wiki h2 {
     color: #aeaeae!important
    }
    .wiki-page .pageactions .wikiaction-current {
     background: #ffffff!important
    }
    .roundfield {
     background: #3d4044!important
    }
    .tabmenu.formtab a {
     text-shadow: none!important
    }
    .formtabs-content {
     border-color: #74787c!important
    }
    .infobar {
     background: none!important
    }
    .goldvertisement .inner {
     background-color: #121317!important
    }
    .thing.spam {
     background: none!important; border: #d2b1b2 1px solid!important;
    }
    .icon-menu a {
     background: none!important
    }
    .entry .buttons li.reported-stamp {
     color: #8f732a!important
    }
    .flair, .linkflairlabel {
     background: #62696a!important; font-size: 9px!important; color: #d6d6d6!important; height: 14px!important;
    }
    .thing .title.loggedin {
     color: #A3DFD3!important
    }
    .thing .title.loggedin.click {
     color: #3d4044!important
    }
    .message.message-reply.recipient>.entry .head, .message.message-parent.recipient>.entry .head {
     color: #777!important
    }
    .message.recipient>.entry .noncollapsed {
     color: #aeaeae!important
    }
    .message.message-reply .entry, .message.message-parent .entry {
     border-color: #656B6C!important
    }
    .message .subject .correspondent {
     background-color: #202328!important
    }
    .message .subject .correspondent a, .message.message-parent .subject {
     color: #ffffff!important
    }
    .message.message-parent .tagline, .message.message-reply .tagline {
     color: #afe1f9!important
    }
    body.with-listing-chooser .listing-chooser {
     background: #202328!important
    }
    .listing-chooser li.selected {
     border-color: #888d90!important; background: #3f4246!important; box-shadow: -30px 0 30px -15px rgba(0,0,0,0.5) inset,0 2px 6px -1px rgba(0,0,0,0.2)!important;
    }
    .listing-chooser li {
     border-width: 1px medium 1px 1px!important; background: #3d4044!important; border-color: #383e42!important;
    }
    .listing-chooser .intro {
     background: #74787c!important; color: #D9DDDD!important; border-color: #D9DDDD!important;
    }
    body.with-listing-chooser .listing-chooser.initialized>.contents {
     position: fixed!important
    }
    body.with-listing-chooser .listing-chooser .grippy {
     background: #272b30!important; border-left: 1px solid #393f43!important;
    }
    body.with-listing-chooser .listing-chooser .grippy:after {
     border-right: 1px solid #202328!important
    }
    body.with-listing-chooser .listing-chooser .grippy:hover:after {
     background: #333639!important
    }
    .listing-chooser .create button {
     background: none repeat scroll 0 0 #202328!important; border-color: #141519!important; box-shadow: 0 1px 0 #3A3C41 inset,0 2px 3px #202328!important; color: #dddddd!important;
    }
    .srdrop {
    background-color: #121317;
    }
}
