css:
@-moz-document regexp("http://.*greenteapress.com/.*/html/.*") {
html, body, table, tr, td {
	margin: 0;
	padding: 0;
}

body {
	margin-left: 10px;
}

body > table > tbody > tr > td:first-child + td {
	width: 80% !important;
	padding-left: 15px !important;
	padding-right: 15px !important;
}


body > table > tbody > tr > td + td + td {
	width: 15% !important;
	background-color: #B0B0B0;
	color: #151515;
	padding: 10px;
	border-left: 1px solid #202020;
	border-top: 1px solid #202020;
	margin-right: 0;
	font-size: 1rem;
} 

td + td + td h4 {
    font-family: 'Concourse T4', 'Fira Sans OT', 'DejaVu Sans', Verdana, sans-serif;
}

td + td + td p {
	background-color: #B0B0B0;
	color: #151515;
  
}

h1, h2, h3, h4, h5, h6 {
    font-family: 'Equity Text A', 'Bitstream Charter', 'DejaVu Serif', Georgia, serif;
}

body, div, p, ol, ul, li {
	font-family: 'Concourse T4', 'Fira Sans OT', 'DejaVu Sans', Verdana, sans-serif;
  font-size: 1.2rem;
	background-color: #F5F5F5;
  color: #151515
}

pre {
	font-family: 'Triplicate T4C', 'Source Code Pro', Menlo, 'DejaVu Sans Mono', Consolas, monospace;
	font-size: 1rem;
	background-color: #202020;
	padding: 10px;
	color: #D0D0D0;
	border-radius: 10px !important;
}

tt {
	font-family: 'Triplicate T4C', 'Source Code Pro', Menlo, 'DejaVu Sans Mono', Consolas, monospace;
	font-size: 1rem;
	background-color: #E0E0E0;
	padding-left: .25em;
	padding-right: .25em;
}
}
