css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nyaa.eu"),
domain("sukebei.nyaa.eu"),
domain("nyaa.se"),
domain("sukebei.nyaa.se") {

html{
background:#333 !important;
color:#ddd !important;
/*text-shadow:rgba(0, 0, 0, 0.7) 1px 1px 0px !important;*/
}
    
#main{
padding-top: 38px!important;
        
}

div > [style*="float:left"]> a > img{
display:none !important;
}

div > [style*="float:right"] > a > img{
display:none !important;
}

#topbar{
background:rgba(0, 0, 0, 0.0) !important;
background-color: #222!important;
z-index:2 !important;
}

#feedBody, #feedHeaderContainer{
background:rgba(0, 0, 0, 0) !important;
background-color: #222!important;
color:#ddd !important;
border:0px !important;
border-radius:10px !important;
}

.feedBackground{
background:transparent !important;
color:#ddd !important;
}

#feedTitleText{
border-bottom:1px dotted #ddd !important;
}

.tlistsize{
border:0px rgba(0, 0, 0, 0.0) !important;
}

.tlistrow{
border-bottom:1px solid #666!important;
border-top: none !important;
/*box-shadow:rgba(255, 255, 255, 0.2) 0px 1px 0px inset !important;*/
}

a{
color:#ddd !important;
}
    
a:hover{
color:rgba(200, 200, 200, 0.6) !important; 
text-decoration: none;
     
}

td b{
color:#3d658e !important;
}

body{
background:transparent !important;
}

.content, .viewdescription, .viewfiletable, textarea, .comment{
    /*background-color: rgba(0,0,0,0.0) !important;*/
    background-color: #222!important;
    -moz-appearance:none !important;
    color:#ddd !important;
    border:0px solid !important;
    border-radius: 2px;
}

.content{
width:auto !important;

}

.quote, .spoiler{
background-color: rgba(255,255,255,0.1) !important;
-moz-appearance:none !important;
color:#ddd !important;
border:0px solid !important;
}

.cmain{
border-top:1px solid #555 !important;
padding-top:3px !important;
color:#ddd !important;
}

.chead span{
color:#3d658e !important;
}
    
.chead a span:hover{
color:#ccc !important;
}

.fileentryname, .fileentrysize{
border-bottom:1px dotted #777 !important;
}

table.viewtable a span{
color: #ddd !important;
}
    

.commentformdiv input{
-moz-appearance:none !important;
margin:2px !important;
border:0px !important;
background-color: #222!important;
color:#ddd !important;
border-radius:2px !important;
}

.commentformdiv input:hover{
background-color:rgba(107,177,237,0.5) !important;
cursor:pointer !important;
}

.page:hover{
background-color:rgba(144, 176, 240, 0.25) !important;
border:1px solid #ccc !important;
}

.page{
border-color:rgba(255, 255, 255, 0.2) !important;
}

.page{
padding:2px !important;
}

.trusted.tlistrow{
background-color:#294C39 !important;
background-color:rgba(68, 113, 85, 0.5) !important;
}

.remake.tlistrow{
background-color:rgba(255, 31, 22, 0.1) !important;
}

.aplus.tlistrow{
background-color:rgba(61, 101, 142, 0.5) !important;
}

.tlistsn, .viewsn{
color:#54A154 !important;
}

.tlistln, .viewln{
color:#E35A61 !important;
}

.tlistmn{
color:#73A8C7 !important;
text-align: right;
padding-right: 5px;
}

.tlisticon a img{
opacity:0.5 !important;
/*display: none;*/
}
    
.tlistdownload a img{
    display: none;
}
.tlistdownload a:before{
content: "â–¼";
}
.tlistfailed {
        border-left: 1px solid #555;
        border-right: 1px solid #555;
        font-size: 0.8em;
}
.tlistsn{
  text-align: right;
} 
 .tlistln{
 text-align: right;
 padding-right: 10px;
}
.tlistdn, .tlistsize{
        text-align: right;
        padding-right: 5px;
    }
td.tlisticon{
        text-align: center;
        font-weight: bold;
        font-size: 0.8em;
}
    
.tlisticon a[title="Anime >> Non-English-translated"]:before{
        content: "Non-English"!important;
        color: #6EC76A;
}   
   
.tlisticon img[alt="Non-English-translated"]{
        display: none!important;
}
    
.tlisticon a[title="Anime >> English-translated"]:before{
        content: "English"!important;
        color: #BE8EE3;
}   
   
.tlisticon img[alt="English-translated"]{
        display: none!important;
}
.tlisticon a[title="Audio >> Lossy"]:before{
        content: "Lossy Audio"!important;
        color: #E35A61;
}   
   
.tlisticon img[alt="Lossy"]{
        display: none!important;
}
.tlisticon a[title="Audio >> Lossless"]:before{
        content: "Lossless Audio"!important;
        color: #E3DE93;
}   
   
.tlisticon img[alt="Lossless"]{
        display: none!important;
}
.tlisticon a[title="Anime >> Raw"]:before{
        content: "Raw Anime"!important;
        color: #999;
}   
   
.tlisticon img[alt="Raw"]{
        display: none!important;
}
.tlisticon a[title="Live Action >> Raw"]:before{
        content: "Raw Live Action"!important;
        color: #E35A61;
}   
   
.tlisticon img[alt="Raw Live Action"]{
        display: none!important;
}
.tlisticon a[title="Books >> Raw"]:before{
        content: "Raw Books"!important;
        color: #96C78E;
}
    
.tlisticon img[alt="Raw Literature"]{
        display: none!important;       
}
.tlisticon a[title="Literature >> Raw"]:before{
        content: "Raw Literature"!important;
        color: #22B087;
}
        
.tlisticon a[title="Literature >> English-translated"]:before{
        content: "English Lit"!important;
        color: #CCC;
}
.tlisticon img[alt="Non-English-translated Literature"]{
        display: none!important;       
}
.tlisticon a[title="Literature >> Non-English-translated"]:before{
        content: "Non-English Lit"!important;
        color: #C46A31;
}
   
.tlisticon img[alt="Raw Books"]{
        display: none!important;
}
   
.tlisticon img[alt="English-scanlated Books"]{
        display: none!important;
}
.tlisticon a[title="Books >> English-scanlated Books"]:before{
        content: "Eng Books"!important;
        color: #73E3C0;
}   
   
.tlisticon img[alt="Non-English-scanlated Books"]{
        display: none!important;
}
.tlisticon a[title="Books >> Non-English-scanlated Books"]:before{
       content: "Non-Eng Books"!important;
       color: #E3AB7F;
}   
   
.tlisticon img[alt="Anime Music Video"]{
        display: none!important;
}  
.tlisticon a[title="Anime >> Anime Music Video"]:before{
        content: "AMV"!important;
        color: #BE8EE3;
} 
.tlisticon img[alt="Idol/Promotional Video"]{
        display: none!important;
}  
.tlisticon a[title="Live Action >> Idol/Promotional Video"]:before{
        content: "Live Action PV"!important;
        color: #E3CB60;
}
.tlisticon img[alt="English-translated Live Action"]{
        display: none!important;
}  
.tlisticon a[title="Live Action >> English-translated"]:before{
        content: "Eng Live Action"!important;
        color: #E3DE93;
}
.tlisticon a[title="Live Action >> Non-English-translated"]:before{
        content: "Non-Eng Live Action"!important;
        font-size: 0.9em;
        color: #73C3E3;
}
.tlisticon img[alt="Games"]{
        display: none!important;
}  
.tlisticon a[title="Software >> Games"]:before{
        content: "Games"!important;
        color: #6EC5E3;
}
.tlisticon img[alt="Applications"]{
        display: none!important;
}  
.tlisticon a[title="Software >> Applications"]:before{
        content: "Apps"!important;
        color: #5E98E3;
} 
.tlisticon img[alt="Graphics"]{
        display: none!important;
}  
.tlisticon a[title="Pictures >> Graphics"]:before{
        content: "Graphics"!important;
        color: #E39DB8;
} 
.tlisticon img[alt="Photos"]{
        display: none!important;
}  
.tlisticon a[title="Pictures >> Photos"]:before{
        content: "Photos"!important;
        color: #E387C5;
} 
    
.viewdownloadbutton img{
        display: none!important;
}
.viewdownloadbutton a:before{
        content: "Download";
        font-size: 1.5em;
}
.viewdownloadbutton a{
        background-color: #58A15C!important;
        padding: 8px 5px 3px 5px;
        border-radius: 3px;
}
.viewdownloadbutton a:hover{
        background-color: #3D7041!important; 
}
.viewdownloadtxtbutton img{
        display: none!important;
}
.viewdownloadtxtbutton a:before{
        content: ".TXT";
        font-size: 1.5em;
}
.viewdownloadtxtbutton a{
        background-color: #58A15C!important;
        padding: 8px 5px 3px 5px;
        border-radius: 3px;
}
.viewdownloadtxtbutton a:hover{
        background-color: #3D7041!important; 
}
    
.chatboxinner {
     color: #aaa!important;   
}
.chatboxinner span{
     color: #3d658e!important;   
}
    
.chatboxinner span b a{
     color: #DDD!important;   
}
.chatboxinner span b a:hover{
     color: #555!important;   
}
   
   
}
