css:
@-moz-document domain("phoenixjp.net") {
body {
background-color: #000000 !important; 
}

table {
color: #DADADA !important; 
}

div.bienvenue {
color: #DADADA !important;
}

div.titre {
border: 2px solid #DBA00;
background-color: #000000 !important;
}

div.options {
background-color: #000000 !important;
}

div.lefttitle {
border-bottom: 1px solid #DADADA;
color: #DADADA !important;
}

div.righttitle {
border-bottom: 1px solid #DADADA;
color: #DADADA !important;
}

div.news {
background-color: #1D1D1D !important;
border: 1px solid rgb(114, 114, 114);
}

div.newsGlobalTitle {
width: 100%;
border-bottom: 0px solid black;
color: #DADADA !important;
background-color: #0D2030 !important;
}


div.desc_site {
background-color: #25272B !important;
}

div.newsTitleMenu {
background-color: #25272B !important;
}

div.reloadnews {
background-color: #25272B !important;
}

div.presentation {
border: 2px solid #345487;
background-color: #000000 !important;
}

div.menunews {
background-color: #0D2030 !important;
color: #DADADA !important;
}

div.menu_news {
background-color: #0D2030 !important;
}

div.menu_fin {
background-color: black !important;
}

div.copyright {
color: #DADADA !important;
}

a.menulink:visited {
color: red !important;
}

a.menulink:link {
color: #DADADA !important;
}

a:visited {
font-family: Arial;  color: orange;
}

a:link {
font-family: Arial; color: #DADADA;
}

a:hover {
color: #FF0000;
}

a.options:link {
color: #DADADA;
}

div.reagirnewstab {
background-color: #0D2030;
color: #000000;
border: #345487;
}

a.options:visited {
color: orange;
}
}
