@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("newegg.com") {

body, .headerAreaTop, .headerAreaMid, .itmTopNavBg,
#stripeHeaderArea, .foot, .itmViewed, .nav-group-first
{
background: #F59E01 !important;
}
.itmNav, .itmBrowseNav, #bodyCenterArea, .navFlyout, .productCells,
.categoryList, .wrap_cell, .call-to-action, .wrap_pitch, .wrap_inner,
.unit_gallery, .title, .grpBody, #bcaShopWindowSEO, .grpViewed,
.itemAction, .itemCell, .boxQueried, .inactive, #bodyArea, dt,
.article, .boxContent, .active, #bcaBreadcrumbTop, #synopsis,
.v660, .background_F6F0E2, .grpDesc, .boxConstraint, .specTitle,
dl, .grpOffers, .grpPromoPrimary, .grpAction,
#ProductBuzzContainer, .grpEmail
{
background: #000000 !important;
}
a, p, h1, h2, h3, h4, h5, .itmBrowseNav, .itmBrowseNav a,
.title, .wrap_inner li, .promo, dd, .price-current, dl, dt,
.price-save-label, .price-save-percent, .price-was,
.itemText li, .itemPromo, label, li, #wc-aplus h2, #wc-aplus h2 *,
#wc-aplus .wc-rich-features .wc-rich-content-description, 
#wc-aplus .wc-rich-features .wc-rich-content-header,
#wc-aplus p *, #wc-aplus h2.wc-first-header,
#wc-aplus ul li *, #wc-aplus li *, em, .text

{
color: #FFFFFF !important;
}
.wrap_cell 
{
border-left: 1px #FFFFFF dashed !important;
} 
.title:hover
{
text-decoration: underline !important;
}
#mt_FixedRightBanner
{
display: none !important;
}
}
