css:
@-moz-document domain("v.youku.com"){
    
/*youku标题 样式*/
.base .title {color: rgba(255, 216, 0, 1);text-shadow:2px 2px 2px rgba(200,200,0,1)}
    
#qheader_box.yk-header-container{background:rgba(140, 199, 242, 1);}

.comments{background:rgba(174,248,251,0.6)}

.playArea .player{padding:1px;background:#fff;border:1px solid #c4cce0}

#player_sidebar{padding:1px 1px 1px 0;background:#fff;border:1px solid #c4cce0;border-left:0;margin-left:-1px}

.paikeArea{background:#fff;border:1px solid #c4cce0;margin-bottom:15px;padding:10px}

.paikeArea .commentcon{margin-bottom:0}

a,#tabs li,a,.sideTool .handle a,.tab_outer li a,.tab_outer li span,.interactArea .fn strong.stat,.p_title a{color:#3B5998}

.com_share label{color:#3B5998!important}

#tabs li.current,.tab_outer li.current a,.tab_outer li.current span{color:#333}

body{background:#000}

.listArea .nBox .body2{background:#fff}

.commentArea .loginBefore textarea{width:826px}

.commentArea .comment .userPhoto{padding:1px;border:1px solid #c4cce0;background:rgba(174,248,251,0.6)}

.commentArea .videoComment .nBox .head{padding:5px 10px;border-top:1px solid #c4cce0;box-shadow:none}

.commentArea .tab_inner{border-top:1px solid #c4cce0;border-bottom:1px solid #C4CCE0;background:#f2f2f2;box-shadow:inset 0 1px 0 #f9f9fa}

.commentArea .tab_inner .tabs{top:-1px}

.commentArea .tab_inner .tabs li{border-right:1px solid #C4CCE0;height:31px}

.commentArea .tab_inner .tabs li.current a,.commentArea .tab_inner .tabs li.current a:hover{border-color:#C4CCE0;background:#fff}

.commentArea .tab_inner .tabs li a,.tab_inner li,.tab_inner{border-color:#C4CCE0}

.commentArea .tab_inner .tabs li a{border-width:1px}

.commentArea .tab_inner .tabs li a:hover{background:transparent;border-color:#C4CCE0}

.commentArea .comment{border-color:#c4cce0}

.commentArea .commentAction{margin-bottom:15px}

.commentArea .commentAction{padding:10px;background:#fff;border:1px solid #c4cce0}

.commentArea .commentAction .userPhoto{padding:1px;border:1px solid #C4CCE0}

.commentArea .commentAction .wordlimit{top:10px;right:10px}

.commentArea .commentAction textarea{border-color:#C4CCE0;width:542px}

.commentArea .commentAction textarea:focus{border-color:#3B5998}

.s_miniHeader{background:-webkit-gradient(linear,center top,center bottom,from(#FFF),to(#F0F1F4));border:0;border-bottom:1px solid #afbddd;box-shadow:inset 0 -1px 0 #F9F9FA,0 1px 0 0 #D9DFEA}

.nBox .head{background:#f2f2f2;border:1px solid #C4CCE0;box-shadow:inset 0 1px 0 #F9F9FA;color:#3B5998}

.nBox .body{border:1px solid #C4CCE0;border-top:none}

.form_btn{border-radius:0}

.sideTool .handle a{border-radius:0;border:1px solid #C4CCE0;background:#f2f2f2;box-shadow:inset 0 1px 1px #F9F9FA}

.sideTool .handle a:hover{background:-webkit-gradient(linear,center top,center bottom,from(#e7ebf2),to(#e4e8f0));border:1px solid #C4CCE0}

.light_switch a{background:#000;border-radius:0;border-color:#fff}

.light_switch a:hover{border:1px solid #fff}

.left .nBox .tab_outer li,.mainCol .nBox .tab_outer li,.nBox .tab_outer li,.form_btnsub_m{border-color:#C4CCE0}

.form_btn .form_btn_text{border-radius:0}

.left .nBox .head,.mainCol .nBox .head,.interactArea .fn_expand,.interactArea .fn_hover{border-color:#c4cce0}

.s_footer .so{background:#f2f2f2;border:1px solid #C4CCE0;box-shadow:inset 0 1px 0 #F9F9FA}

.s_footer .links{border:1px solid #C4CCE0;border-top:0}

.showCatalog_S .sotool .sobtn{background:#f2f2f2;border:1px solid #C4CCE0;margin-left:5px}

.showCatalog_S .sotool .sobtn .btn{background:#f2f2f2;box-shadow:inset 0 1px 0 #F9F9FA;color:#3B5998;font-size:13px}

.showCatalog_S .sotool .sobtn .btn:hover,.showCatalog_S .sotool .sobtn:hover{background:-webkit-gradient(linear,center top,center bottom,from(#e7ebf2),to(#e4e8f0));text-decoration:none}

.showCatalog_S .sotool .sotxt{border-color:#c4cce0}

.showCatalog_S .sotool .sotxt:focus{border-color:#3B5998}

.interactArea .panel{background:#fff;border:1px solid #c4cce0;box-shadow:none}

.interactArea .fn label,.interactArea .fn em.short{color:#444}

.commentArea .returntop{background:#fff;padding:1px;border:1px solid #c4cce0}

.commentArea .returntop a{background:#f2f2f2}

.commentArea .returntop a:hover{background:-webkit-gradient(linear,center top,center bottom,from(#e7ebf2),to(#e4e8f0))}

.sokutool input:focus{border-color:#3B5998}

.vrank .order .top3{background:#3B5998}

#fn_stat{margin-right:10px!important}

#videocomment{margin-bottom:15px}

.listArea .pack_list li.current{background-color:#D8DFEA;color:#444}

.listArea .pack_list li .show_title a:hover{background-color:#eff2f7}

.qPager .turn a,.qPager .turn span{border-color:#c4cce0}

.qPager .turn a:hover,.commentArea .qPager .pages li a:hover{border-color:#3B5998}

.page_thx .commentArea .commentAction textarea{width:762px}

.interactArea .fn .ico_up{width:16px;height:15px;background:url(http://imgly.net/img/XJRYG.gif) no-repeat -17px 0;top:5px}

.interactArea .fn .ico_down{width:16px;height:15px;background:url(http://imgly.net/img/XJRYG.gif) no-repeat 0 0;top:8px}

.vh{padding-left:155px}

.v .v_thumb{left:10px;width:135px}

.v .v_time{left:10px}

.colllist1w .v,.colllist1w .p{margin-bottom:10px}

.toolbar .tool .handle a{color:#000;text-decoration:none}

.toolbar .tool .insert_share .label{float:left;color:#E0122B;padding-top:10px;_padding-top:11px;display:block;margin-right:10px;width:50px}

.page_thx .commentArea .commentAction textarea{width:762px;color:#1400ff;background:#bcfc91}

.yk-videoinfo .text{background: rgba(112, 225, 255, 1)}

.yk-videoinfo .time{height:20px;font-size:14px;color:rgba(255,0,0,1)}

.colllist1w .items{background:rgba(188,234,255,0.8)}

.commentArea .commentAction{background:transparent!important}

#videocomment,.returntop,.returntop a{background:rgba(200,218,200,0.4)!important}

.yk-header-container .yk-guide .handle{background:rgba(110,209,242,1);}
    
.yk-header-container .yk-guide .handle a {color: rgba(0, 0, 0, 1);}

.yk-header-container .yk-guide .panel{background:rgba(188,234,255,1)}

}
/*soku搜索视频 模式*/
@-moz-document url-prefix("http://www.soku.com/search_video/"){
    body{background-color: rgba(0,0,0,1)}    
    .sk_masthead{background-color: rgba(0,0,0,1)}    
    .sk_footer{background-color: rgba(0,0,0,1)}
 }
