css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://thoughtmaybe.com") {
    #body{
        background-color: #333!important;
    }
    .video-item h3 a, .maincolor2, .light-title, .video-item h3 a{
        color: #3B73B5!important;
    }
    
    .video-item h3 a:hover, .video-item .item-author a{
        color: #555!important;
    }
    
    .video-listing-filter .btn-default, .video-listing-filter .btn-group, .widget-border{
        border-color:#444!important;
    }
    .video-listing-filter .btn-default, .widget_tag_cloud a{
        background-color: #555!important;
        text-shadow:none!important;
        color:#ccc!important;
    }
    .video-listing-filter .btn-default:hover, .widget_tag_cloud a:hover{
        background-color: #444!important;
        color:#aaa!important;
    }
}
