@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("^https\:\/\/myspace\.com\/(.+)$"),
  regexp("^https\:\/\/myspace\.com\/(.+)\/(.+)$"), 
  url("https://myspace.com/")
{

    /*Text Stuff*/
    
    textarea,
    span,
    p,
    nav,    div,
    label,
    input,
    div > time,
    button,
    body,
    a:link,
    .song > a > h3,
    .left,
    .dotted,
    #links,
    #hCard > ul,
    #hCard > section > div > h3,
    #addItemsToMix > h1, 
    #artistProfileWidget > div > h3, 
    dd, 
    dt, 
    h1, 
    #similarSongs h3{
    color:#f1f1f1 !important; text-shadow: 0px 1px 1px #000000 !important;
    }
     

    .dotted,
    .flex,
    footer, 
    .topLeft{
    color:#000000 !important;
    }
       
    h2,
    h4,
    h5,
    h6,
    .dotted
    {color: #f1f1f1 !important;}
    
    /*End Text Stuff*/
    /*Alignment Stuff*/
    
    #history-search{
        height:32px !important;
    }
    
    .beam-queue #library .table > .heading,
    .beam-queue #musicQueue .table > .heading{
    border-top: 15px solid #232323 !important;
    }
    
    #sidebar > nav ul,
    #sidebar > nav,
    #player,
    #sidebar > nav ul > li > a{
    border-bottom:1px solid #000000 !important;
    border-top: 1px solid #590000 !important;
    }
    
    #sidebar{
    box-shadow:0 0 3px 1px rgba(255, 0, 0, 0.45) !important;
    }
    
    #global-nav .meter{
    border-bottom: 1px solid #343434 !important;
    }
    

    #global, 
    #lightboxCover{
    bottom:0px !important;
    }
    
    #drawer{
    padding-bottom:57px !important;
    }
    
    #background,
    .horizontalContent{
    bottom:-20px !important;
    }
    
    .chat.popout{
    bottom:15px !important; left:-17px !important;
    }
    
    #footer{
    left:20px !important;
    }
    
    #notificationsPopout {
    bottom:73px !important;
    left:11px !important;
    }
    
    #global-nav > nav{
    margin-right:10px !important;
    }
    
    /*End Alignment Stuff*/
    /*Background Theme Stuff*/
       
    footer,
    a > .title,
    .tip-inner,
    .threads,
    .POVs > ul > li,
    .portfolioItem > div > div,
    .nameplate,
    .liveLabel,
    .isotope .gradient:after,
    .gradient:after,
    .connectButton,
    .addToExisting > ul > li:hover,
    #videoPlayer > div > .dynamicUI > .liveStreamDrawer,
    #tab-content > article,
    #radioSearch,
    #radio .options li,
    #profileWidget,
    #mixes .list li.util,
    #messaging .list.options > li,
    #message,
    #drawer.message,
    #drawer{
