    textarea,
    input,
    header,
    form,
    footer > .thirdParties,
    dl,
    aside > p,
    aside > div,
    aside,
    .topFansIntro > header,
    .threads,
    .threads > ul > li.unread,
    .thirdParties > li,
    .table,
    .superpost > footer,
    .stats.top,
    .settingsAccordion > form,
    .settingsAccordion,
    .settings > div,
    .selected ,
    .provider > i,
    .privacy_terms > section,
    .privacy_terms,
    .multi > div > header > p,
    .multi > div > header > h2,
    .multi > div,
    .mediaItems > li,
    .formFooter > .primary,
    .flex,
    .ellipsis:hover,
    .dropDown > ul,
    .dropDown > strong,
    .createAlbum,
    .artistInfoTray,
    #wrap > nav,
    #videoPlayer.live > div > div > footer,
    #userid209419,
    #threadSummary,
    #streamNav > div > button,
    #recommended-people,
    #profileImageUpload > strong,
    #privacyForm,
    #notificationsForm,
    #mediaUploader,
    #medialist > ol > .heading,
