    .grid > li > span, 
    .grid > .empty, 
    .stats, 
    .dotted.top,
    #sidebar > footer > #btnMoreInfo:hover{
    background-color: #232323 !important;}
  
    .rr,
    .rr > .stats,
    .message > div,
    .listContainer,
    .addComment > .text,
    #sidebar > nav > ul > li.selected,
    #sidebar > nav > ul > .hasSubNav > ul,
    #sidebar > nav,
    #sidebar > header,
    #sidebar > footer > #btnMoreInfo,
    #sidebar > footer,
    #sidebar > div,
    #popover,
    #interact, 
    .top.chatPopup{
    background-color: #343434 !important;}
    
    .messageGroup.self > .sender + .message > div, 
    #artistProfileWidget{
    background-color:#350000 !important;
    }
    
    form,
    button,
    aside,
    article,
    .thirdParty,
    .textarea,
    .primary,
    .popout,
    .notification,
    .messages,
    .lightbox > header,
    .homeSearchTray,
    .formFooter,
