    }
    
    
    a:after, 
    .trailer{
    background-color:#000000 !important; background-image:linear-gradient(#d8d8d8, #ffffff) !important;
    }
    
    
    #global-nav .meter > .buffer{
    background-color:#b20000 !important; opacity: 0.7 !important; background-image:linear-gradient(#FF5900, #FF6F21) !important;
    }
     
    .noStyle:hover, 
    .sign-up-sign-in > button:hover, 
    #playControls > button:hover{
    background-color:#b20000 !important; opacity: 0.7 !important;
    }
    
    #global-nav .meter{
    background-color:#000000 !important; opacity: 0.5 !important; background-image:linear-gradient(#000000, #000000) !important;
    }
    
    
    
    
    #superPost, 
    #streamFilterBtn > button, 
    #sidebar > footer > #btnMoreInfo{
    box-shadow:0 0 7px 0 rgba(255, 0, 0, 0.45) !important;
    }
    
    
    #global-nav > nav > ul > li.selected > a {
    border-bottom-color: #377778 !important;
    }
       
    #profileImageUpload > ul > li:hover > button,
    .primary:hover, 
    .threads > ul > li:hover, 
    footer > ul > li > a:hover, 
    #messaging .list.active > li.online.open{
    background-image:linear-gradient(#67191a, #67191a) !important;
    }
    
    #profileImageUpload > ul > li > button,
    #drawer .list .selected,
    #drawer .list li:hover, 
    #confirmDialog, 
    #confirmDialog > .actions{
    background-image:linear-gradient(#535353, #535353) !important;
    }
        
    #featuredArtistTray{
