css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix('https://www.tumblr.com/')   {
body {
   background:url("http://static.tumblr.com/bda39246cc5cfeaa89fcddd4a6360b13/pts5sph/xtUn437bd/tumblr_static_55bh4oc7hv8c8og8gsgkks8w4.png")no-repeat fixed  !important;
   background-color: #972C16!important;
   background-position: 0% 60% !important;
   background-size: 100% 100%  !important;
 }

div.l-header .tab.iconic a{
  background-color: rgba(245, 193, 115,1.0) !important;
  border-radius: 60px !important;  
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {  
   font-family: Arial; color: #000;
  background-color: rgba(245, 193, 115,.75) !important;

 }


.notification  {
  	background: #972C16!important; border: none !important; 
	color: #000000 !important; font-size: 11px !important;
  opacity:0.8;
  }

.notification :link, .notification :active, .notification :visited {
  	color: #F3E6C7 !important;
    text-decoration: none !important;
    border: none !important;
  }

.new_post_label .new_post_label_icon {
background-image: url('https://31.media.tumblr.com/fe3d2bc10c5ade73bfd330187c9f0f10/tumblr_n4363cqT5X1txyyupo3_1280.png') !important;
background-size: 520px 100px !important;
margin-top: 0px !important;
margin-right: 15px !important;
opacity: 1 !important;
}

div.l-content #new_post .new_post_label {
color: rgba(151,44,22,.9) !important;
line-height: 1.4em;
font-weight: bold !important;
font-size: .9em !important;

}

#right_column .controls_section li a,

#right_column .controls_section li a .count {

  color: #972C16 !important; 
}

#logo, #logo a {
  position: relative;
  z-index: 1;
  width: 270px !important;
  height: 71px !important;
  background-repeat: no-repeat;
  background: url("https://31.media.tumblr.com/83f7d5c708a0a6d2eefad4725663792f/tumblr_n43688XaEr1txyyupo1_250.png")no-repeat !important;
}

.controls_section li .posts:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.posts  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .posts:before {
  content: "Yen" !important;
}.controls_section li .followers:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.followers  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .followers:before {
  content: "Friends" !important;
}.controls_section li .drafts:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.drafts  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .drafts:before {
  content: "Jobs" !important;
}.controls_section li .queue:before {
    content: "Queue" !important;

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.queue  div.hide_overflow{
  visibility: hidden !important;
}

.new_post_label{
font-size:0px !important;
}
.controls_section.user_list li a:before { content: none!important; }

.right_column:after { content: none!important; }

a.radar_avatar:before { content: none!important; }

.post_permalink { 
  	height: 16px!important;
    width: 16px!important;
	margin: 5px;
    border-radius: 5px!important;
	background-color: #ddd!important;
  	background-image: none!important;
}

.post_permalink:before {
  	top: 0px!important;
    right: 0px!important;
    content: none!important;
    border: none!important;
}

.follow { color: inherit!important; }

#dashboard_controls a, #dashboard_controls .control {
  	color: #eee!important;
  	text-shadow: #333 1px 1px 1px!important;
}

#dashboard_controls a, .controls_section a .count canvas { 
  	-webkit-filter: grayscale(100%);
	-moz-filter: grayscale(100%);
	filter: grayscale(100%);
}

div.l-header .selection_nipple { border-bottom-color: inherit; }

#pagination a { color: #ccc!important; }
#pagination a:hover { color: #ddd!important; }

.footer_links a { color: #999!important; }


/*.new_post_label_icon{background-image:none !important}*/
.new_post_label > I {display: none}

#new_post{
background: #fff
url(https://31.media.tumblr.com/fe3d2bc10c5ade73bfd330187c9f0f10/tumblr_n4363cqT5X1txyyupo3_1280.png) center no-repeat !important;
/*background-color:#fff !important;*/
}


.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {

  background: none !important;

  width: 0px !important;

  height: 0px !important;

  margin-top: 0px !important;

  border-bottom: solid 11px rgba(245,193,115,.25) !important;

  border-right: 11px solid transparent !important;

  border-left: 11px solid transparent !important;

}
}
