css:
@-moz-document domain("intellinote.net") {
.note-cell-title { white-space: inherit; } /* wrap note titles in center column (default is nowrap) */
.notebook { width: 500px; } /* increase width of center column (default is 300px) */
/* make the left-column navigation bar "auto-hide" - narrow by default, but grow on hover */
.app-sidebar { width: 60px; } /* default is 300px */
.project { margin-left: 60px; } /* default is 300px */
.app-sidebar:hover { width: 300px; } /* not needed by default */
}
