css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("imdb.com") {
* {
font-size: 8pt  !important;
background-color: transparent !important;

}
body {
color:#FFE7C1 !important;
background-color: #5c5c5c !important;
font-family: verdana, "lucida console", sans-serif  !important;
}
a:link {
text-decoration: none !important;
color: #FFC682!important;
}
a:visited {
text-decoration: none !important;
color: #CBBDAE !important;
}
a:hover {
color:black !important;
text-decoration: none !important;
background-color:lightgray !important;
}
#wrapper {
background:none !important;
}

/*-------- comments ----------------*/

td[bgcolor="#ffffff"] {
background: black none !important;
}
td[bgcolor="#ffffdb"], 
td[bgcolor="#dfdfdf"],
div.yn *{
background: #4c4c4c none !important;
} 
td[bgcolor="#006699"] {
background: #3c3c3c none !important;
}
font[color="#000000"] {
color: #bbbbbb !important;
}
#tn15main table.boards th {
background: #4c4c4c none !important;
}

/*---------- sidebar -----------------*/

#tn15lhs h6 {            /* sidebar titles */
background-image: none !important;
margin-top: 0 !important;
}
#tn15lhs h6 span {             /* sidebar titles */
  visibility: visible !important;
}
#tn15lhs .link.selected a{
font-weight: bold !important;
color: white !important;
text-decoration: underline !important;
}
img[alt="[Add to My Movies]"]{
opacity: 0.40 !important;
}

/*---------- main section -------------*/

#tn15title h1 {           /* movie title */
color:#FFE7C1 !important;
font-size: 200% !important;
font-weight: normal !important;
display: inline !important;
background: #3c3c3c none !important;
}
#tn15title em {
color:#FFE7C1 !important;
}
#tn15title * {           /* movie title */
letter-spacing: normal !important;
}
/*--- movie rating ---*/
#tn15rating{
padding-left: 3px !important;
border-left: 3px solid #3c3c3c !important;
}
.starbar{
border: 0 !important;
}
#votep + b + b {           /* movie title */
font-weight: bold !important;
font-size: 130% !important;
background: #3c3c3c none !important;
}
.starbar .outer {
border-right: 3px solid #575757 !important;
}
.starbar .outer, .starbar #starbar, .starbar a:hover {
  background-image: url(data:image/gif;base64,) !important;
}
/*--- gold links under movie title ---*/
#tn15 .toplinks img[alt="*"]{
background-color: #ffffff !important;
}
.tn15more {
background: #515151 none !important;
padding-right: 0px !important;
}
a.tn15more:after {
content:" Â»" !important;
}
/*--- cast list ---*/
tr.even td {
background-color: #5a5a5a !important;
}
tr.odd td {
background-color: #5e5e5e !important;
}
/*--- misc ---*/
div#tn15shopbox {             /* amazon shop box */
display: none !important;
}
b, b a {             /* some things that need to stand out */
background-color: #525252 !important;
}
img[alt="IMDbTV"] {             /* IMDbTV image for TV airing */
display: none !important;
}
/*--- blue category images ---*/
img[alt="Overview"], 
img[alt="Cast"], 
img[alt="Additional Details"], 
img[alt="FAQ"], 
img[alt="User Comments"],
img[alt="Message Boards"], 
img[alt="Fun Stuff"], 
img[alt="Recommendations"],
img[alt="Filmography"]{
display: none !important;
}
a[name="comment"] + a {
left: 0px !important;
top: 0px !important;
}

/*-------------- header -----------------*/

#nb15tabs, #nb15home, #nb15go {
display: none !important;
}
#nb15 {
margin-top:-40px !important;
margin-bottom:0px !important;
}
#nb15 div {
background: #454545 none !important;
-moz-border-radius: 0 0 7px 7px!important;
}
td[background],  /* all tds with a background attribute */
iframe,
#tn15adrhs,               /* swf ad */
img[height="28"][width="28"] {      /* img in view trailer page */
display: none !important;
}

div#swf_supertab_02, .imdb_lb {
display: none !important;
}
form select, 
form input:not([type="image"]) {     /* not the yellow buttons */
color: #FFC682 !important;
background: #3c3c3c none !important;
border-color: black !important;
}
/*-------------- trailers page ------------*/
#playeriframe iframe {    /* only view iframe on trailers page */
display: block !important;
}
#drawercontents {
background-color: black !important;
}

/*-------------- view pictures page ------------*/
table.g_image {
background: #3c3c3c none !important;
-moz-border-radius: 7px !important;
}
table.g_image * {
background: transparent none !important;
}

/*--------------  ------------*/


}
