css:
@-moz-document url-prefix(http://www.tumblr.com/),url-prefix(https://www.tumblr.com/){

#popover_blogs .tab_notice:after {
    display: none;
}
  
div.l-header .iconic>a {
    background-image: url('https://68.media.tumblr.com/69f10e8635a72fcd292ed1a5c959bd25/tumblr_inline_nxmm9mDA061qiqsac_540.png') !important;
}
.tab_bar .iconic a.tab_anchor {
    background-size: 310px 35px;
}
.tab.iconic > .tab_anchor::before {
    content: none !important;
}
#inbox_button a,
#discover_button a,
#home_button a,
#activity_button a {
    height: 0;
    width: 0;
    top: 0px;
    padding-left: 25px;
    padding-top: 36px;
}
.tab_bar .tab_messaging.with_notice .tab_anchor {    background-position: -40px 0}
.tab_bar .tab_activity .tab_anchor {    background-position: -220px 0}
.tab_bar .tab_inbox .tab_anchor {    background-position: -125px 0}
.tab_bar .tab_discover .tab_anchor {    background-position: -85px 0}
.tab_bar .tab_home .tab_anchor {    background-position: -44px 0}
.tab_bar .tab.iconic:hover .tab_anchor,
.tab_bar .tab.iconic:active .tab_anchor {
    opacity: .6;
}
div#new-xkit-control a.tab_anchor {
    height: 0 !important;
    width: 0 !important;
    top: 6px !important;
    padding-right: 28px !important;
    padding-top: 30px !important;
    background: url('https://68.media.tumblr.com/69f10e8635a72fcd292ed1a5c959bd25/tumblr_inline_nxmm9mDA061qiqsac_540.png') !important;
    background-position: 45px 0px !important;
}

#messaging_button {
    padding-right: 10px !important;
	background: url("https://68.media.tumblr.com/76f73364bde0fd8a2578e092648a185f/tumblr_inline_nxmmi0Li5A1qiqsac_540.png") !important;
	top: -3px !important;
    background-size: 50px 36px !important;
    opacity: .6;
}

}
