css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("youtube.com"),
regexp("https?://apis\.google\.com/.*/widget/render/comments\?.*\&first_party_property=YOUTUBE.*"),
regexp("https://plus\.google\.com/.*/widget/render/comments\?.*")
{


span[class*="input"]
{
border-color: rgba(255,0,0,.3) !important;
}


/*-----------------------------------------------------------------------------------*/
/*Clear Canvas*/

div:not([class*="banner"]):not([class*="ytp"]):not([class*="html5"]):not([class*="tip"]):not([class*="video"]):not(.ytcenter-lights-off-overlay),
span:not([class*="videowall-still"]):not([class*="input"]):not(.thumb),
a:not([class*="videowall-still"]),
label,
a,
hr,
h1,h2,h3,h4,h5,
table,
tr,
td,
ul,
li,
#appbar-help,
#appbar-guide-button,
[class*="vm-video-actions"]
{
text-shadow:
   none !important;

background:
   rgba(0,0,0,0) !important;

border:
   0px
   solid
   rgba(0,0,0,0) !important;

box-shadow:
   0px
   0px
   0px
   0px
   rgba(0,0,0,0) !important;
}

div
{
color:
   rgba(255,255,255,.65) !important;
}

body
{
background:
/* Shadowing */
   -moz-linear-gradient(to right,
      rgba(0,0,0,1),
      rgba(0,0,0,0),
      rgba(0,0,0,0),
      rgba(0,0,0,0),
      rgba(0,0,0,0),
      rgba(0,0,0,1)),
   -moz-linear-gradient(to bottom,
      rgba(0,0,0,0),
      rgba(0,0,0,1)),
/* Blend Layer */
   -moz-radial-gradient(center bottom,
      rgba(75,255,0,.7),
      rgba(75,255,0,.5),
      rgba(0,0,0,0)),
/* Base Layer */
      rgba(0,0,255,1) !important; /*---------------------------Choose your favorite color here---------------------------*/

background-attachment:
   fixed !important;
}

a:hover
{
text-decoration:
   none !important;
}

a.yt-uix-redirect-link,
a span.title, 
a span.yt-ui-ellipsis-wrapper,
a.yt-uix-sessionlink,
span.watch-title,
#appbar-help,
h2.epic-nav-item-heading,
.yt-uix-button span,
.pl-header-details li,
.feed-item-snippet-additional-info,
a .branded-page-module-title-text,
h1,
h2,
h3,
h4,
.Mb .R4 span
{
color:
   rgba(255,255,255,.4) !important;

transition:
   color 1s ease-in-out .5s;
}

a.yt-uix-redirect-link:hover,
a span:hover.title, 
a span:hover.yt-ui-ellipsis-wrapper,
a.yt-uix-sessionlink:hover,
span.watch-title:hover,
#appbar-help:hover,
h2.epic-nav-item-heading:hover,
.yt-uix-button:hover span,
.branded-page-module-title,
a .branded-page-module-title-text:hover,
.selected,
.Mb .R4:hover span
{
color:
   rgba(255,255,255,1) !important;

text-decoration:
   none !important;

transition:
   color .3s ease-in-out 0s;
}

a span.stat, 
.yt-lockup-deemphasized-text
{
color:
   rgba(255,255,255,.3) !important;
}

h2.epic-nav-item-heading
{
border-bottom: 
   5px 
   solid 
   rgba(255,255,255,.5) !important;
}

.has-custom-banner
{
border:
   none !important;
}







/*-----------------------------------------------------------------------------------*/
/*Masthead*/

#ticker
{
background:
/* Shadowing */
   -moz-linear-gradient(to bottom,
      rgba(0,0,0,0),
      rgba(0,0,0,0)),
/* Base Layer */
      rgba(50,50,50,1) !important;
}

#yt-masthead-container
{
background:
/* Shadowing */
   -moz-linear-gradient(to bottom,
      rgba(0,0,0,0),
      rgba(0,0,0,0),
      rgba(0,0,0,.25),
      rgba(0,0,0,.5))
/* Base Layer */
      rgba(50,50,50,1) !important;

border-color:
   rgba(0,0,0,0)
   rgba(0,0,0,0)
   rgba(255,255,255,.1)
   rgba(0,0,0,0) !important;
border-width:
   0px
   0px
   1px
   0px !important;

transition:
   border-color 2s ease-in-out 2s !important;
}

#yt-masthead-container:hover
{
border-color:
   rgba(0,0,0,0)
   rgba(0,0,0,0)
   rgba(255,0,0,1)
   rgba(0,0,0,0) !important;

transition:
   border-color 2s ease-in-out 0s !important;
}

#masthead-expanded,
#appbar-nav
{
background:
/* Shadowing */
   -moz-linear-gradient(to bottom,
      rgba(0,0,0,0),
      rgba(0,0,0,.5)),
/* Base Layer */
      rgba(50,50,50,1) !important;
}

#appbar-guide-button, #appbar-settings-button, .yt-uix-button, .Mb .R4 span
{
background:
/* Shadowing */
   -moz-linear-gradient(to bottom,
      rgba(255,255,255,.3),
      rgba(0,0,0,0),
      rgba(0,0,0,.7),
      rgba(0,0,0,1)),
/* Base Layer */
      rgba(70,70,70,1) !important;

border-color:
   rgba(255,255,255,.3)
   rgba(255,255,255,.1)
   rgba(0,0,0,.5)
   rgba(255,255,255,.1) !important;
border-width:
   1px
   1px
   1px
   1px !important;

opacity:
   1 !important;

transition:
   border-top-color 2s ease-in-out 2.0s,
   border-right-color 2s ease-in-out 3s,
   border-bottom-color 2s ease-in-out 3.5s,
   border-left-color 2s ease-in-out 3s,
   opacity 2s ease-in-out 4s !important;
}

#appbar-guide-button:hover, .yt-uix-button:hover, .Mb .R4:hover span
{
border-color: 
   rgba(255,0,0,.9)
   rgba(255,0,0,.3)
   rgba(255,0,0,.3)
   rgba(255,0,0,.2) !important;
border-width:
   1px
   1px
   1px
   1px !important;

opacity:
   1.00 !important;

transition:
   border-top-color .7s ease-in-out 0s,
   border-right-color .7s ease-in-out .1s,
   border-bottom-color .7s ease-in-out .2s,
   border-left-color .7s ease-in-out .3s,
   opacity .1s ease-in-out 0s !important;
}

#appbar-settings-button:hover
{
border-color:
   rgba(255,0,0,.9)
   rgba(255,0,0,.3)
   rgba(255,0,0,.3)
   rgba(255,0,0,.2) !important;
border-width:
   1px
   1px
   1px
   1px !important;

opacity:
   1.00 !important;

transition:
   border-top-color .7s ease-in-out 0s,
   border-left-color .7s ease-in-out .1s,
   border-bottom-color .7s ease-in-out .2s,
   border-right-color .7s ease-in-out .4s,
   opacity .1s ease-in-out 0s !important;
}

#search-btn
{
background:
/* Shadowing */
   -moz-linear-gradient(to bottom,
      rgba(255,255,255,.3),
      rgba(0,0,0,0),
      rgba(0,0,0,.7),
      rgba(0,0,0,1)),
/* Base Layer */
      rgba(70,70,70,1) !important;

border-color:
   rgba(255,255,255,.3)
   rgba(255,255,255,.1)
   rgba(0,0,0,.5)
   rgba(255,255,255,.1) !important;
border-width:
   1px
   1px
   1px
   0px !important;

opacity:
   1 !important;

transition:
   border-color 2s ease-in-out 4s,
   opacity 2s ease-in-out 4s !important;
}

#search-btn:hover
{
border-color:
   rgba(255,255,255,.7)
   rgba(255,255,255,.1)
   rgba(255,255,255,.1)
   rgba(255,255,255,.5) !important;
border-width:
   1px
   1px
   1px
   0px !important;

opacity:
   1.00 !important;

transition:
   border-color .3s ease-in-out 0s,
   opacity .3s ease-in-out 0s !important;
}

#appbar-nav .yt-uix-button,
.feed-header .yt-uix-button,
button.yt-uix-button-action-menu,
button.yt-uix-button-hover-action-menu,
#channel-subheader .yt-uix-button,
#watch7-action-buttons .yt-uix-button
{
background:
   rgba(0,0,0,0) !important;

border: 
   0px !important;
}

.addto-watch-later-button,
.addto-watch-later-button-success
{
opacity: 
   .2 !important;

transition: 
   .2s ease-in-out 0s !important;
}

.contains-addto:hover .addto-watch-later-button,
.contains-addto:hover .addto-watch-later-button-success
{
opacity: 
   1 !important;

transition: 
   .2s ease-in-out 0s !important;
}

.yt-badge
{
color: 
   rgba(255,255,255,1) !important;

background:
   -moz-linear-gradient(to bottom,
      rgba(255,0,0,1),
      rgba(0,0,0,1)) !important;

border:
   1px
   solid
   rgba(255,255,255,.1) !important;
border-radius:
   10px !important;
}







/*-----------------------------------------------------------------------------------*/
/*Search Bar*/

#masthead-search-terms
{
border-color:
   rgba(255,255,255,.1)
   rgba(255,255,255,.1)
   rgba(255,255,255,.1)
   rgba(255,255,255,.1) !important;
border-width:
   1px
   0px
   1px
   1px !important;

transition:
   border-color 2s ease-in-out 4s !important;
}

#masthead-search-terms:hover
{
border-color:
   rgba(255,255,255,.5)
   rgba(255,255,255,.5)
   rgba(255,255,255,.5)
   rgba(255,255,255,.5) !important;
border-width:
   1px
   0px
   1px
   1px !important;

transition:
   border-color .3s ease-in-out 0s !important;
}

#masthead-search-term
{
color:
   rgba(255,255,255,.3) !important;

transition:
   color 4s ease-in-out 0s !important;
}

#masthead-search-term:hover, #masthead-search-term:focus:hover
{
color:
   rgba(255,255,255,1) !important;

transition:
   color .3s ease-in-out 0s !important;
}

#masthead-search-term:focus
{
color:
   rgba(255,0,0,.5) !important;

transition:
   color 4s ease-in-out 0s !important;
}





/*-----------------------------------------------------------------------------------*/
/*Search Results & Settings Pulldown*/

.gssb_m,
#appbar-settings-menu,
ul.yt-uix-button-menu
{
background:
/* Shadowing */
   -moz-linear-gradient(to bottom,
      rgba(0,0,0,0),
      rgba(0,0,0,.5)),
/* Base Layer */
      rgba(50,50,50,.5) !important;
}

.gssb_m:before,
#appbar-settings-menu:before,
ul.yt-uix-button-menu:before
{
display: block;
width: 100%;
height: 100%;

position: absolute;
top: -0px;

content: "";

background:
/* Shadowing */
   -moz-linear-gradient(to bottom,
      rgba(0,0,0,0),
      rgba(0,0,0,0),
      rgba(0,0,0,.5)),
/* Base Layer */
      rgba(50,50,50,1);

opacity: 0;

z-index: -1;

transition:
   opacity 4s ease-in-out 1s;
}

#appbar-settings-menu:before
{
background:
/* Shadowing */
   -moz-linear-gradient(to bottom,
      rgba(0,0,0,0),
      rgba(0,0,0,0),
      rgba(0,0,0,.5)),
/* Base Layer */
      rgba(50,50,50,1);

z-index: -1;
}

ul.yt-uix-button-menu:before
{
background:
/* Shadowing */
   -moz-linear-gradient(to bottom,
      rgba(0,0,0,0),
      rgba(0,0,0,0),
      rgba(0,0,0,.5)),
/* Base Layer */
      rgba(50,50,50,1);

z-index: -1;
}

.gssb_m:hover:before,
#appbar-settings-menu:hover:before,
ul.yt-uix-button-menu:hover:before
{
opacity: 1;

transition:
   opacity .3s ease-in-out 0s;
}

.gssb_m > tbody > tr:nth-child(odd), 
#appbar-settings-menu > li:nth-child(odd),
ul.yt-uix-button-menu > li:nth-child(odd)
{
background:
   rgba(0,0,0,.2) !important;
}

#appbar-settings-menu > li a, 
.gssb_m span, .gssb_m b, 
ul.yt-uix-button-menu span
{
color:
   rgba(255,255,255,.4) !important;

transition:
   color 1s ease-in-out .5s;
}

#appbar-settings-menu > li a:hover, 
.gssb_m > tbody > tr:hover span, 
.gssb_m > tbody > tr:hover b, 
ul.yt-uix-button-menu li:hover span
{
color:
   rgba(255,255,255,1) !important;

transition:
   color .3s ease-in-out 0s;
}

ul.yt-uix-button-menu li:hover span
{
background:
   rgba(0,0,0,0) !important;
}

#advanced-search-button-menu, [class*="vm-video-actions"]
{
background:
/* Shadowing */
   -moz-linear-gradient(to bottom,
      rgba(0,0,0,0),
      rgba(0,0,0,0),
      rgba(0,0,0,.5)),
/* Base Layer */
      rgba(50,50,50,1) !important;
}







/*-----------------------------------------------------------------------------------*/
/*Guide Container*/

#guide-container a
{
color: rgba(255,255,255,.1) !important;

transition:
   color 4s ease-in-out 4s !important;
}

#guide-container:hover a
{
color: rgba(255,255,255,.4) !important;

transition:
   color .3s ease-in-out 0s !important;
}

#guide-container:hover a span
{
color: rgba(255,255,255,.4) !important;

transition:
   color .3s ease-in-out .2s !important;
}

#guide-container a:hover span
{
color: rgba(255,255,255,1) !important;

transition:
   color .3s ease-in-out 0s !important;
}

#guide-container a.guide-item:before
{
display: block;
width: 100%;
height: 100%;

position: absolute;
top: 0px;
left: 0px;

content: "";

background:
   -moz-linear-gradient(to right,
      rgba(0,0,0,0),
      rgba(0,0,0,0),
      rgba(0,0,0,0),
      rgba(0,0,0,.5));

box-shadow:
   0px
   0px
   1px
   1px
   rgba(255,0,0,0)
   inset;

opacity: 0;

transition:
   opacity 1s ease-in-out .2s,
   box-shadow 1s ease-in-out 0s;
}

#guide-container a.guide-item:hover:before
{
box-shadow:
   0px
   0px
   1px
   1px
   rgba(255,0,0,.5)
   inset;

opacity: 1;

transition:
   opacity 1s ease-in-out 0s,
   box-shadow 3s ease-in-out 0s;
}

#guide-container
{
background:
/* Shadowing */
   -moz-linear-gradient(to left,
      rgba(0,0,0,0),
      rgba(0,0,0,.5)),
   -moz-linear-gradient(to bottom,
      rgba(0,0,0,0),
      rgba(0,0,0,.5)),
/* Base Layer */
      rgba(50,50,50,0) !important;

border-color:
   rgba(255,255,255,.5)
   rgba(255,255,255,.1)
   rgba(255,255,255,.1)
   rgba(255,255,255,.5) !important;
border-width:
   0px
   1px
   1px
   0px !important;

transition:
   border-color 2s ease-in-out 4s !important;
}

#guide-container:hover
{
border-color:
   rgba(255,255,255,.5)
   rgba(255,0,0,.5)
   rgba(255,0,0,.5)
   rgba(255,255,255,.5) !important;
border-width:
   0px
   1px
   1px
   0px !important;

transition:
   border-color .3s ease-in-out 0s !important;
}

#guide-container:before
{
display: block;
width: 100%;
height: 99.9%;

position: absolute;
top: 0px;
left: -1px;

content: "";

background:
/* Shadowing */
   -moz-linear-gradient(to left,
      rgba(0,0,0,0),
      rgba(0,0,0,0),
      rgba(0,0,0,0),
      rgba(0,0,0,.25),
      rgba(0,0,0,1)),
   -moz-linear-gradient(to bottom,
      rgba(0,0,0,0),
      rgba(0,0,0,0),
      rgba(0,0,0,1)),
/* Base Layer */
      rgba(50,50,50,1);

opacity: 0;

transition:
   opacity 4s ease-in-out 5s;
}

#guide-container:hover:before
{
opacity: 1;

transition:
   opacity .3s ease-in-out 0s;
}


#yt-admin-sidebar-hh
{
background:
/* Shadowing */
   -moz-linear-gradient(to left,
      rgba(0,0,0,0),
      rgba(0,0,0,0),
      rgba(0,0,0,.5)),
/* Base Layer */
      rgba(50,50,50,.5) !important;

box-shadow:
   2px
   2px
   5px
   2px
   rgba(0,0,0,.5) !important;
}






/*-----------------------------------------------------------------------------------*/
/*Recommended Video List*/

#watch7-content
{
background:
/* Shadowing */
   -moz-linear-gradient(to bottom,
      rgba(0,0,0,.2),
      rgba(0,0,0,.2)),
/* Base Layer */
      rgba(50,50,50,0) !important;

border-color:
   rgba(255,255,255,.1)
   rgba(255,255,255,.1)
   rgba(255,255,255,.1)
   rgba(255,255,255,.1) !important;
border-width:
   0px
   0px
   0px
   0px !important;
}

#watch7-sidebar
{
background:
/* Shadowing */
   -moz-linear-gradient(to bottom,
      rgba(0,0,0,.6),
      rgba(0,0,0,.6)),
/* Base Layer */
      rgba(50,50,50,0) !important;

border-color:
   rgba(255,255,255,.1)
   rgba(255,255,255,.1)
   rgba(255,255,255,.1)
   rgba(255,255,255,.1) !important;
border-width:
   0px
   0px
   0px
   0px !important;
}





/*-----------------------------------------------------------------------------------*/
/*Video Editor*/

#yt-admin-content
{
border:
   solid
   1px
   rgba(255,255,255,.05) !important;
}

#top-bar
{
border-bottom:
   solid
   1px
   rgba(255,255,255,.05) !important;
}

#preview-box
{
background:
   -moz-linear-gradient(to bottom,
      rgba(0,0,0,.1),
      rgba(0,0,0,.7)) !important;
}

#mediapicker .editor-tab-container
{
background:
   -moz-linear-gradient(to bottom,
      rgba(255,255,255,.02),
      rgba(255,255,255,.05)) !important;
}

#mediapicker .editor-tab-container .menu-tab
{
border-top:
   5px
   solid
   rgba(0,0,0,0) !important;

border-right:
   5px
   solid
   rgba(0,0,0,0) !important;

border-left:
   5px
   solid
   rgba(0,0,0,0) !important;
}

#mediapicker .editor-tab-container .menu-tab:hover
{
border-bottom:
   5px
   solid
   rgba(255,255,255,.3) !important;
}

#mediapicker .editor-tab-container .selected
{
border-bottom:
   5px
   solid
   rgba(255,255,255,.3) !important;
}

.yt-uix-form-input-fluid
{
border:
   solid
   1px
   rgba(255,255,255,.05) !important;

transition:
   border .3s ease-in-out 0s !important;
}

.yt-uix-form-input-fluid:hover
{
border:
   solid
   1px
   rgba(255,255,255,.5) !important;

transition:
   border .3s ease-in-out 0s !important;
}

#storyboard
{
background:
   -moz-linear-gradient(to bottom,
      rgba(255,255,255,.02),
      rgba(255,255,255,.01)) !important;

border:
   1px
   solid
   rgba(255,255,255,.03) !important;
}

#storyboard .timeline-playhead-line
{
background:
   rgba(255,255,255,.4) !important;
}

#storyboard .timeline-playhead-thumb
{
border-color:
   rgba(255,0,0,1) !important;
}

#storyboard .timeline-playhead-time
{
background:
   rgba(0,0,0,1) !important;

border:
   1px
   solid
   rgba(255,255,255,.3) !important;
}

#storyboard .hline,
#storyboard .stick
{
background:
   rgba(255,255,255,.3) !important;
}

#storyboard .mtick
{
background:
   rgba(255,255,255,.05) !important;
}






/*-----------------------------------------------------------------------------------*/
/*Comment Thread*/

.Mb, .TD, .Ub, 
.Mb .REa .RN
{
background:
   rgba(0,0,0,0) !important;
}

.Mb .RN
{
background:
   rgba(0,0,0,1) !important;
}

.Mb div, .uPc
{
color: 
   rgba(255,255,255,.3) !important;
}

.proflinkWrapper span, .proflinkWrapper a, .WR span
{
color: 
   rgba(255,255,255,.5) !important;

transition:
   color 1s ease-in-out .5s;
}

.Mb div, .Mb div:hover, .Mb span:hover, .Mb a:hover
{
text-decoration:
   none !important;
}

.Ub, .Kgb, .YR span
{
color: 
   rgba(255,255,255,.5) !important;

transition:
   color 1s ease-in-out .5s;
}

.Mb a:hover, 
.Mb span:hover a, 
.Mb a:hover span, 
.WR:hover span, 
.Ig:hover .gj, 
.Kv:hover, 
.YR:hover span,
.Mb .RN:hover .d-A-B,
.Al:hover .gj,
.xJ:hover .gj
{
color: 
   rgba(255,255,255,1) !important;

transition:
   color .3s ease-in-out 0s;
}

.Mb .ot-anchor
{
color:
   rgba(180,0,0,1) !important;
}





/*-----------------------------------------------------------------------------------*/
/*Misc*/

.feed-item-container
{
margin-top:
   10px !important;
box-shadow:
   2px
   2px
   5px
   2px
   rgba(0,0,0,.5) !important;
}

.feed-item-container:before
{
display: block;
width: 99.9%;
height: 99.9%;

position: absolute;
top: 0px
;
left: 0px;

content: "";

background:
/* Shadowing */
   -moz-linear-gradient(to bottom,
      rgba(0,0,0,0),
      rgba(0,0,0,0),
      rgba(0,0,0,.7)),
/* Base Layer */
      rgba(50,50,50,.8);

border: 1px solid rgba(0,0,0,0);

opacity: 0;

z-index: -1;

transition:
   opacity 1s ease-in-out 1s,
   border-top-color .5s ease-in-out 0.5s,
   border-right-color 1s ease-in-out .5s,
   border-left-color 1s ease-in-out .5s,
   border-bottom-color 2.5s ease-in-out .5s;
}

.feed-item-container:hover:before
{
border: 1px solid red;

opacity: 1;

transition:
   opacity 1s ease-in-out 0s,
   border-top-color .7s ease-in-out .3s,
   border-right-color .7s ease-in-out .4s,
   border-bottom-color .7s ease-in-out .5s,
   border-left-color .7s ease-in-out .6s;
}


#insight .GK5ROLOBPN
{
background:
   rgba(0,0,0,.7) !important;

box-shadow:
