/* Shadowing */
   -moz-linear-gradient(to bottom,
      rgba(0,0,0,0),
      rgba(0,0,0,0),
      rgba(0,0,0,.7)),
/* Base Layer */
      rgba(50,50,50,.5) !important;

border-color:
   rgba(255,255,255,.1)
   rgba(255,255,255,.1)
   rgba(255,255,255,.1)
   rgba(255,255,255,.1) !important;
border-width:
   1px
   1px
   0px
   1px !important;
}

